import React from 'react';
import {
  ProductsContainer,
  ProductGrid,
  ProductCard,
  ProductImage,
  ProductInfo,
  ProductName,
  ProductPrice,
  ProductActions,
  ActionButton,
  QuickViewButton,
  WishlistButton,
  OutOfStock,
  SaleBadge,
  NewBadge
} from './styles/Products.styles';
import { ShoppingCart, Visibility, Favorite } from '@mui/icons-material';
import { useCart } from '../context/CartContext';
import { Link } from 'react-router-dom';

const Products = ({ products, filters = {}, sort = 'newest' }) => {
  const { addToCart } = useCart();

  // Filter products based on selected filters
  const filteredProducts = products.filter(product => {
    if (filters.color && product.color !== filters.color) return false;
    if (filters.size && product.size !== filters.size) return false;
    return true;
  });

  // Sort products based on selected sort option
  const sortedProducts = [...filteredProducts].sort((a, b) => {
    switch (sort) {
      case 'asc':
        return a.price - b.price;
      case 'desc':
        return b.price - a.price;
      case 'newest':
      default:
        return new Date(b.createdAt || 0) - new Date(a.createdAt || 0);
    }
  });

  const handleAddToCart = (product, e) => {
    e.preventDefault();
    e.stopPropagation();
    addToCart(product, 1);
  };

  const handleQuickView = (productId, e) => {
    e.preventDefault();
    e.stopPropagation();
    // Quick view functionality can be implemented here
    console.log('Quick view for product:', productId);
  };

  const handleAddToWishlist = (productId, e) => {
    e.preventDefault();
    e.stopPropagation();
    // Wishlist functionality can be implemented here
    console.log('Add to wishlist:', productId);
  };

  if (sortedProducts.length === 0) {
    return (
      <ProductsContainer>
        <div style={{ 
          textAlign: 'center', 
          padding: '60px 20px',
          color: '#666'
        }}>
          <h3>No products found</h3>
          <p>Try adjusting your filters to see more products.</p>
        </div>
      </ProductsContainer>
    );
  }

  return (
    <ProductsContainer>
      <ProductGrid>
        {sortedProducts.map((product) => (
          <ProductCard key={product.id}>
            <Link to={`/product/${product.id}`}>
              <ProductImage src={product.img} alt={product.name} />
              
              {/* Product Badges */}
              {product.onSale && <SaleBadge>Sale</SaleBadge>}
              {product.isNew && <NewBadge>New</NewBadge>}
              {product.stock === 0 && <OutOfStock>Out of Stock</OutOfStock>}
              
              <ProductInfo>
                <ProductName>{product.name}</ProductName>
                <ProductPrice>
                  {product.originalPrice && (
                    <span className="original-price">${product.originalPrice}</span>
                  )}
                  <span className="current-price">${product.price}</span>
                </ProductPrice>
                
                <ProductActions>
                  <ActionButton 
                    onClick={(e) => handleAddToCart(product, e)}
                    disabled={product.stock === 0}
                    title="Add to Cart"
                  >
                    <ShoppingCart />
                  </ActionButton>
                  
                  <QuickViewButton 
                    onClick={(e) => handleQuickView(product.id, e)}
                    title="Quick View"
                  >
                    <Visibility />
                  </QuickViewButton>
                  
                  <WishlistButton 
                    onClick={(e) => handleAddToWishlist(product.id, e)}
                    title="Add to Wishlist"
                  >
                    <Favorite />
                  </WishlistButton>
                </ProductActions>
              </ProductInfo>
            </Link>
          </ProductCard>
        ))}
      </ProductGrid>
    </ProductsContainer>
  );
};

export default Products;
