import styled from 'styled-components';

export const CartContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
`;

export const CartHeader = styled.div`
  margin-bottom: 30px;
  text-align: center;
`;

export const CartTitle = styled.h1`
  font-size: 2.5rem;
  font-weight: 300;
  color: #333;
  margin-bottom: 10px;
`;

export const CartContent = styled.div`
  display: grid;
  grid-template-columns: 2fr 1fr;
  gap: 30px;

  @media (max-width: 968px) {
    grid-template-columns: 1fr;
    gap: 20px;
  }
`;

export const CartItems = styled.div`
  display: flex;
  flex-direction: column;
  gap: 15px;
`;

export const CartSummary = styled.div`
  position: sticky;
  top: 20px;
  height: fit-content;
`;

export const SummaryCard = styled.div`
  background: white;
  border-radius: 12px;
  padding: 25px;
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
  border: 1px solid #e0e0e0;
`;

export const SummaryTitle = styled.h2`
  font-size: 1.5rem;
  font-weight: 600;
  color: #333;
  margin-bottom: 20px;
  padding-bottom: 15px;
  border-bottom: 2px solid #f0f0f0;
`;

export const SummaryRow = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 15px;
  padding: 8px 0;
`;

export const SummaryLabel = styled.span`
  color: #666;
  font-size: 1rem;
`;

export const SummaryValue = styled.span`
  color: #333;
  font-weight: 500;
  font-size: 1rem;
`;

export const TotalRow = styled(SummaryRow)`
  border-top: 2px solid #f0f0f0;
  padding-top: 15px;
  margin-top: 10px;
  
  ${SummaryLabel} {
    font-weight: 600;
    font-size: 1.1rem;
    color: #333;
  }
  
  ${SummaryValue} {
    font-weight: 700;
    font-size: 1.2rem;
    color: #00796b;
  }
`;

export const CheckoutButton = styled.button`
  width: 100%;
  padding: 15px;
  background: #00796b;
  color: white;
  border: none;
  border-radius: 8px;
  font-size: 1.1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  margin-top: 20px;

  &:hover {
    background: #00695c;
    transform: translateY(-2px);
    box-shadow: 0 4px 12px rgba(0, 121, 107, 0.3);
  }

  &:disabled {
    background: #ccc;
    cursor: not-allowed;
    transform: none;
    box-shadow: none;
  }
`;

export const ContinueShoppingButton = styled.button`
  width: 100%;
  padding: 12px;
  background: transparent;
  color: #00796b;
  border: 2px solid #00796b;
  border-radius: 8px;
  font-size: 1rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.3s ease;
  margin-top: 10px;

  &:hover {
    background: #00796b;
    color: white;
    transform: translateY(-2px);
  }
`;

export const EmptyCart = styled.div`
  text-align: center;
  padding: 60px 20px;
  max-width: 500px;
  margin: 0 auto;
`;

export const EmptyCartIcon = styled.div`
  width: 100px;
  height: 100px;
  background: #f8f9fa;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0 auto 30px;
  color: #ccc;

  svg {
    font-size: 48px;
  }
`;

export const EmptyCartTitle = styled.h2`
  font-size: 2rem;
  font-weight: 300;
  color: #333;
  margin-bottom: 15px;
`;

export const EmptyCartText = styled.p`
  color: #666;
  font-size: 1.1rem;
  line-height: 1.6;
  margin-bottom: 30px;
`;

export const ClearCartButton = styled.button`
  padding: 10px 20px;
  background: #ffebee;
  color: #d32f2f;
  border: 1px solid #ffcdd2;
  border-radius: 6px;
  cursor: pointer;
  font-size: 0.9rem;
  font-weight: 500;
  transition: all 0.3s ease;
  margin-top: 10px;

  &:hover {
    background: #d32f2f;
    color: white;
  }
`;
