import React from 'react';
import { Container, Circle, Image, Info, Icon } from './styles/Product.styles';
import { ShoppingCartOutlined, SearchOutlined, FavoriteBorderOutlined } from '@mui/icons-material';
import { useCart } from '../context/CartContext';
import { Link } from 'react-router-dom';

const Product = ({ item }) => {
  const { addToCart } = useCart();

  const handleAddToCart = () => {
    addToCart(item, 1);
  };

  // Use _id from MongoDB or id from other databases
  const productId = item._id || item.id;

  return (
    <Container>
      <Circle />
      <Image src={item.image || item.img} alt={item.name} />
      <Info>
        <Icon onClick={handleAddToCart}>
          <ShoppingCartOutlined />
        </Icon>
        <Icon>
          <Link to={`/product/${productId}`}>
            <SearchOutlined />
          </Link>
        </Icon>
        <Icon>
          <FavoriteBorderOutlined />
        </Icon>
      </Info>
    </Container>
  );
};

export default Product;
