import React from "react";
import styled from "styled-components";
import Product from "./Product";

const Container = styled.div`
  padding: 40px 20px;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  gap: 25px;
  max-width: 1400px;
  margin: 0 auto;
`;

const Title = styled.h1`
  text-align: center;
  margin: 20px 0 40px 0;
  font-size: 2.5rem;
  font-weight: 300;
  width: 100%;
  color: #2d3748;
  position: relative;
  
  &::after {
    content: '';
    display: block;
    width: 80px;
    height: 3px;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    margin: 15px auto;
    border-radius: 2px;
  }
`;

const LoadingContainer = styled.div`
  text-align: center;
  width: 100%;
  padding: 80px 20px;
  color: #718096;
  font-size: 1.1rem;
`;

const ProductCount = styled.div`
  text-align: center;
  width: 100%;
  color: #4CAF50;
  font-size: 16px;
  font-weight: 600;
  margin-bottom: 20px;
  background: #f8fff8;
  padding: 10px;
  border-radius: 8px;
  border: 1px solid #e8f5e8;
`;

const Products = ({ products, loading }) => {
  if (loading) {
    return (
      <Container>
        <Title>FEATURED PRODUCTS</Title>
        <LoadingContainer>
          <i className="fas fa-spinner fa-spin" style={{ fontSize: '2rem', marginBottom: '15px', display: 'block' }}></i>
          Loading amazing products...
        </LoadingContainer>
      </Container>
    );
  }

  return (
    <Container>
      <Title>OUR PRODUCTS</Title>
      <ProductCount>
        üõçÔ∏è Showing {products.length} Amazing Products
      </ProductCount>
      {products.map((item) => (
        <Product item={item} key={item.id} />
      ))}
    </Container>
  );
};

export default Products;
