import React from "react";
import styled from "styled-components";
import axios from "axios";

const Info = styled.div`
  opacity: 0;
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  left: 0;
  background-color: rgba(0, 0, 0, 0.2);
  z-index: 3;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.5s ease;
  cursor: pointer;
`;

const Container = styled.div`
  flex: 1;
  margin: 5px;
  min-width: 280px;
  height: 350px;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: #f5fbfd;
  position: relative;
  
  &:hover ${Info} {
    opacity: 1;
  }
`;

const Circle = styled.div`
  width: 200px;
  height: 200px;
  border-radius: 50%;
  background-color: white;
  position: absolute;
`;

const Image = styled.img`
  height: 75%;
  z-index: 2;
  max-width: 100%;
  object-fit: contain;
`;

const Icon = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background-color: white;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 10px;
  transition: all 0.5s ease;
  cursor: pointer;
  
  &:hover {
    background-color: #e9f5f5;
    transform: scale(1.1);
  }
`;

const Product = ({ item }) => {
  console.log('Product data:', item);
  
  const addToCart = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login to add items to cart');
      return;
    }

    try {
      const base64Payload = token.split('.')[1];
      const payload = JSON.parse(atob(base64Payload));
      const userId = payload.userId;

      const cartItem = {
        productId: item.id,
        quantity: 1,
        price: item.price,
        title: item.title,
        img: item.img
      };

      await axios.post(
        `${process.env.REACT_APP_API_URL}/api/cart/${userId}`,
        cartItem,
        { 
          headers: { 
            Authorization: `Bearer ${token}` 
          } 
        }
      );
      
      alert('Product added to cart!');
    } catch (error) {
      console.error('Error adding to cart:', error);
      alert('Failed to add product to cart');
    }
  };

  // Use product image or fallback
  const imageUrl = item.img || `https://via.placeholder.com/300x300/4A90E2/FFFFFF?text=${encodeURIComponent(item.title || 'Product')}`;
  
  return (
    <Container>
      <Circle />
      <Image 
        src={imageUrl} 
        alt={item.title}
        onError={(e) => {
          e.target.src = `https://via.placeholder.com/300x300/4A90E2/FFFFFF?text=${encodeURIComponent(item.title || 'Product')}`;
        }}
      />
      <Info>
        <Icon onClick={addToCart}>
          <i className="fas fa-shopping-cart"></i>
        </Icon>
        <Icon>
          <i className="fas fa-search"></i>
        </Icon>
        <Icon>
          <i className="fas fa-heart"></i>
        </Icon>
      </Info>
    </Container>
  );
};

export default Product;
