import React, { useState, useEffect } from 'react';
import {
  Container,
  Title,
  OrderList,
  OrderItem,
  OrderHeader,
  OrderInfo,
  OrderStatus,
  ProductList,
  ProductItem,
  ProductImage,
  ProductDetails,
  ProductPrice,
  EmptyState,
  Loading,
  Error
} from './styles/Orders.styles';
import { orderService } from '../services/orderService';
import { useAuth } from '../context/AuthContext';

const Orders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { user } = useAuth();

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        setLoading(true);
        const data = await orderService.getOrders();
        setOrders(data);
        setError('');
      } catch (error) {
        console.error('Error fetching orders:', error);
        setError('Failed to load orders. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    if (user) {
      fetchOrders();
    }
  }, [user]);

  if (loading) {
    return (
      <Container>
        <Loading>Loading your orders...</Loading>
      </Container>
    );
  }

  if (error) {
    return (
      <Container>
        <Error>{error}</Error>
      </Container>
    );
  }

  return (
    <Container>
      <Title>Your Orders</Title>
      {orders.length === 0 ? (
        <EmptyState>
          <h2>No orders found</h2>
          <p>You haven't placed any orders yet.</p>
          <button 
            onClick={() => window.location.href = '/'}
            style={{
              padding: '12px 24px',
              backgroundColor: 'teal',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer'
            }}
          >
            Start Shopping
          </button>
        </EmptyState>
      ) : (
        <OrderList>
          {orders.map((order) => (
            <OrderItem key={order._id}>
              <OrderHeader>
                <OrderInfo>
                  <h3>Order #{order._id.slice(-8).toUpperCase()}</h3>
                  <p>Placed on {new Date(order.createdAt).toLocaleDateString()}</p>
                  <p>Total: ${order.amount}</p>
                </OrderInfo>
                <OrderStatus status={order.status || 'processing'}>
                  {order.status || 'Processing'}
                </OrderStatus>
              </OrderHeader>
              <ProductList>
                {order.products.map((product, index) => (
                  <ProductItem key={index}>
                    <ProductImage 
                      src={product.img || 'https://images.unsplash.com/photo-1560769629-975ec94e6a86?w=100'} 
                      alt={product.name} 
                    />
                    <ProductDetails>
                      <h4>{product.name}</h4>
                      <p>Quantity: {product.quantity}</p>
                      <p>Color: {product.color || 'Black'} | Size: {product.size || 'M'}</p>
                    </ProductDetails>
                    <ProductPrice>${product.price}</ProductPrice>
                  </ProductItem>
                ))}
              </ProductList>
            </OrderItem>
          ))}
        </OrderList>
      )}
    </Container>
  );
};

export default Orders;
