import React, { useState } from "react";
import styled from "styled-components";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Container = styled.div`
  flex: 1;
  margin: 15px;
  min-width: 280px;
  max-width: 320px;
  height: 480px;
  display: flex;
  flex-direction: column;
  align-items: center;
  background: white;
  position: relative;
  padding: 0;
  box-shadow: 0 4px 15px rgba(0,0,0,0.1);
  border-radius: 12px;
  cursor: pointer;
  transition: all 0.3s ease;
  border: 1px solid #f0f0f0;
  
  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 25px rgba(0,0,0,0.15);
  }
`;

const ImageContainer = styled.div`
  position: relative;
  width: 100%;
  height: 250px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
  border-radius: 12px 12px 0 0;
  overflow: hidden;
`;

const Image = styled.img`
  height: 200px;
  max-width: 85%;
  object-fit: contain;
  transition: transform 0.3s ease;
  
  ${Container}:hover & {
    transform: scale(1.05);
  }
`;

const WishlistIcon = styled.div`
  position: absolute;
  top: 15px;
  right: 15px;
  width: 36px;
  height: 36px;
  border-radius: 50%;
  background: white;
  display: flex;
  align-items: center;
  justify-content: center;
  box-shadow: 0 2px 8px rgba(0,0,0,0.15);
  cursor: pointer;
  transition: all 0.3s ease;
  z-index: 2;
  
  &:hover {
    background: #ff4757;
    color: white;
    transform: scale(1.1);
  }
`;

const ProductDetails = styled.div`
  width: 100%;
  padding: 20px;
  text-align: left;
  background: white;
  border-radius: 0 0 12px 12px;
  flex-grow: 1;
  display: flex;
  flex-direction: column;
`;

const ProductTitle = styled.h3`
  font-size: 16px;
  font-weight: 600;
  margin: 0 0 8px 0;
  color: #2d3748;
  line-height: 1.4;
  height: 45px;
  overflow: hidden;
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
`;

const ProductPrice = styled.p`
  font-size: 20px;
  font-weight: bold;
  color: #2d3748;
  margin: 0 0 15px 0;
`;

const SelectionContainer = styled.div`
  width: 100%;
  margin: 8px 0;
`;

const SelectLabel = styled.label`
  display: block;
  font-size: 12px;
  font-weight: 600;
  color: #718096;
  margin-bottom: 5px;
  text-transform: uppercase;
  letter-spacing: 0.5px;
`;

const Select = styled.select`
  width: 100%;
  padding: 10px 12px;
  border: 2px solid #e2e8f0;
  border-radius: 8px;
  font-size: 14px;
  background: white;
  transition: all 0.3s ease;
  
  &:focus {
    outline: none;
    border-color: #4299e1;
    box-shadow: 0 0 0 3px rgba(66, 153, 225, 0.1);
  }
  
  &:hover {
    border-color: #cbd5e0;
  }
`;

const AddToCartButton = styled.button`
  width: 100%;
  padding: 12px;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  border: none;
  border-radius: 8px;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
  margin-top: auto;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
  
  &:hover {
    background: linear-gradient(135deg, #5a6fd8 0%, #6a4190 100%);
    transform: translateY(-2px);
    box-shadow: 0 4px 12px rgba(102, 126, 234, 0.3);
  }
  
  &:active {
    transform: translateY(0);
  }
  
  &:disabled {
    background: #a0aec0;
    cursor: not-allowed;
    transform: none;
    box-shadow: none;
  }
`;

const SuccessMessage = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background: rgba(72, 187, 120, 0.95);
  color: white;
  padding: 8px 16px;
  border-radius: 20px;
  font-size: 12px;
  font-weight: 600;
  z-index: 10;
  animation: fadeInOut 2s ease-in-out;
  
  @keyframes fadeInOut {
    0% { opacity: 0; transform: translate(-50%, -50%) scale(0.8); }
    20% { opacity: 1; transform: translate(-50%, -50%) scale(1); }
    80% { opacity: 1; transform: translate(-50%, -50%) scale(1); }
    100% { opacity: 0; transform: translate(-50%, -50%) scale(0.8); }
  }
`;

const Product = ({ item }) => {
  const navigate = useNavigate();
  const [selectedSize, setSelectedSize] = useState(item.size && item.size.length > 0 ? item.size[0] : '');
  const [selectedColor, setSelectedColor] = useState(item.color && item.color.length > 0 ? item.color[0] : '');
  const [isAdding, setIsAdding] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);
  
  // Navigate to single product page
  const handleProductClick = () => {
    navigate(`/product/${item.id}`);
  };

  const addToCart = async (e) => {
    if (e) e.stopPropagation(); // Prevent navigating to product page
    
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login to add items to cart');
      return;
    }

    setIsAdding(true);

    try {
      const base64Payload = token.split('.')[1];
      const payload = JSON.parse(atob(base64Payload));
      const userId = payload.userId;

      const cartItem = {
        productId: item.id,
        quantity: 1,
        price: item.price,
        title: item.title,
        img: item.img,
        size: selectedSize,
        color: selectedColor
      };

      await axios.post(
        `${process.env.REACT_APP_API_URL}/api/cart/${userId}`,
        cartItem,
        { 
          headers: { 
            Authorization: `Bearer ${token}` 
          } 
        }
      );
      
      // Show success animation instead of alert
      setShowSuccess(true);
      setTimeout(() => setShowSuccess(false), 2000);
      
    } catch (error) {
      console.error('Error adding to cart:', error);
      alert('Failed to add product to cart');
    } finally {
      setIsAdding(false);
    }
  };

  // Use product image or fallback
  const imageUrl = item.img || `https://via.placeholder.com/300x300/4A90E2/FFFFFF?text=${encodeURIComponent(item.title || 'Product')}`;
  
  return (
    <Container onClick={handleProductClick}>
      <ImageContainer>
        <Image 
          src={imageUrl} 
          alt={item.title}
          onError={(e) => {
            e.target.src = `https://via.placeholder.com/300x300/4A90E2/FFFFFF?text=${encodeURIComponent(item.title || 'Product')}`;
          }}
        />
        
        <WishlistIcon onClick={(e) => e.stopPropagation()}>
          <i className="far fa-heart"></i>
        </WishlistIcon>
        
        {showSuccess && (
          <SuccessMessage>
            <i className="fas fa-check" style={{ marginRight: '5px' }}></i>
            Added to Cart!
          </SuccessMessage>
        )}
      </ImageContainer>
      
      {/* PRODUCT DETAILS WITH SIZE & COLOR SELECTION */}
      <ProductDetails onClick={(e) => e.stopPropagation()}>
        <ProductTitle>{item.title}</ProductTitle>
        <ProductPrice>${item.price}</ProductPrice>
        
        {/* SIZE SELECTION */}
        {item.size && item.size.length > 0 && (
          <SelectionContainer>
            <SelectLabel>Size</SelectLabel>
            <Select 
              value={selectedSize} 
              onChange={(e) => setSelectedSize(e.target.value)}
              onClick={(e) => e.stopPropagation()}
            >
              {item.size.map((size) => (
                <option key={size} value={size}>{size}</option>
              ))}
            </Select>
          </SelectionContainer>
        )}
        
        {/* COLOR SELECTION */}
        {item.color && item.color.length > 0 && (
          <SelectionContainer>
            <SelectLabel>Color</SelectLabel>
            <Select 
              value={selectedColor} 
              onChange={(e) => setSelectedColor(e.target.value)}
              onClick={(e) => e.stopPropagation()}
            >
              {item.color.map((color) => (
                <option key={color} value={color}>{color}</option>
              ))}
            </Select>
          </SelectionContainer>
        )}
        
        {/* ADD TO CART BUTTON */}
        <AddToCartButton 
          onClick={addToCart} 
          disabled={isAdding}
        >
          {isAdding ? (
            <>
              <i className="fas fa-spinner fa-spin"></i>
              Adding...
            </>
          ) : (
            <>
              <i className="fas fa-shopping-cart"></i>
              Add to Cart
            </>
          )}
        </AddToCartButton>
      </ProductDetails>
    </Container>
  );
};

export default Product;
