import React from 'react';
import {
  CartItemContainer,
  CartItemInfo,
  CartItemImage,
  CartItemDetails,
  CartItemControls,
  QuantityControl,
  QuantityButton,
  QuantityDisplay,
  RemoveButton,
  PriceInfo
} from './CartItem.styles';
import { Add, Remove, Delete } from '@mui/icons-material';
import { useCart } from '../../context/CartContext';

const CartItem = ({ item }) => {
  const { updateQuantity, removeFromCart } = useCart();

  const handleQuantityChange = (newQuantity) => {
    if (newQuantity < 1) {
      removeFromCart(item.id);
    } else {
      updateQuantity(item.id, newQuantity);
    }
  };

  const handleRemove = () => {
    removeFromCart(item.id);
  };

  const totalPrice = (item.price * item.quantity).toFixed(2);

  return (
    <CartItemContainer>
      <CartItemInfo>
        <CartItemImage src={item.img} alt={item.name} />
        <CartItemDetails>
          <h3>{item.name}</h3>
          <p>Color: {item.color || 'Black'} | Size: {item.size || 'M'}</p>
          <PriceInfo>
            <span className="unit-price">${item.price} each</span>
            <span className="total-price">${totalPrice} total</span>
          </PriceInfo>
        </CartItemDetails>
      </CartItemInfo>
      
      <CartItemControls>
        <QuantityControl>
          <QuantityButton 
            onClick={() => handleQuantityChange(item.quantity - 1)}
            disabled={item.quantity <= 1}
          >
            <Remove />
          </QuantityButton>
          <QuantityDisplay>{item.quantity}</QuantityDisplay>
          <QuantityButton onClick={() => handleQuantityChange(item.quantity + 1)}>
            <Add />
          </QuantityButton>
        </QuantityControl>
        
        <RemoveButton onClick={handleRemove}>
          <Delete />
          Remove
        </RemoveButton>
      </CartItemControls>
    </CartItemContainer>
  );
};

export default CartItem;
