import React, { useState, useEffect } from "react";
import styled from "styled-components";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Container = styled.div`
  flex: 1;
  margin: 15px;
  min-width: 280px;
  max-width: 320px;
  height: 480px;
  display: flex;
  flex-direction: column;
  align-items: center;
  background: white;
  position: relative;
  padding: 0;
  box-shadow: 0 4px 15px rgba(0,0,0,0.1);
  border-radius: 12px;
  cursor: pointer;
  transition: all 0.3s ease;
  border: 1px solid #f0f0f0;
  
  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 25px rgba(0,0,0,0.15);
  }
`;

const ImageContainer = styled.div`
  position: relative;
  width: 100%;
  height: 250px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
  border-radius: 12px 12px 0 0;
  overflow: hidden;
`;

const Image = styled.img`
  height: 200px;
  max-width: 85%;
  object-fit: contain;
  transition: transform 0.3s ease;
  
  ${Container}:hover & {
    transform: scale(1.05);
  }
`;

const WishlistIcon = styled.div`
  position: absolute;
  top: 15px;
  right: 15px;
  width: 36px;
  height: 36px;
  border-radius: 50%;
  background: white;
  display: flex;
  align-items: center;
  justify-content: center;
  box-shadow: 0 2px 8px rgba(0,0,0,0.15);
  cursor: pointer;
  transition: all 0.3s ease;
  z-index: 2;
  
  &:hover {
    background: #ff4757;
    color: white;
    transform: scale(1.1);
  }
`;

const ProductDetails = styled.div`
  width: 100%;
  padding: 20px;
  text-align: left;
  background: white;
  border-radius: 0 0 12px 12px;
  flex-grow: 1;
  display: flex;
  flex-direction: column;
`;

const ProductTitle = styled.h3`
  font-size: 16px;
  font-weight: 600;
  margin: 0 0 8px 0;
  color: #2d3748;
  line-height: 1.4;
  height: 45px;
  overflow: hidden;
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
`;

const ProductPrice = styled.p`
  font-size: 20px;
  font-weight: bold;
  color: #2d3748;
  margin: 0 0 15px 0;
`;

const SelectionContainer = styled.div`
  width: 100%;
  margin: 8px 0;
`;

const SelectLabel = styled.label`
  display: block;
  font-size: 12px;
  font-weight: 600;
  color: #718096;
  margin-bottom: 5px;
  text-transform: uppercase;
  letter-spacing: 0.5px;
`;

const Select = styled.select`
  width: 100%;
  padding: 10px 12px;
  border: 2px solid #e2e8f0;
  border-radius: 8px;
  font-size: 14px;
  background: white;
  transition: all 0.3s ease;
  
  &:focus {
    outline: none;
    border-color: #4299e1;
    box-shadow: 0 0 0 3px rgba(66, 153, 225, 0.1);
  }
  
  &:hover {
    border-color: #cbd5e0;
  }
`;

const ColorThumbnails = styled.div`
  display: flex;
  gap: 6px;
  margin-top: 5px;
  flex-wrap: wrap;
`;

const ColorThumbnail = styled.div`
  width: 24px;
  height: 24px;
  border-radius: 50%;
  border: 2px solid ${props => props.selected ? '#667eea' : '#e2e8f0'};
  background: ${props => {
    // Handle different color names with appropriate backgrounds
    const color = props.color?.toLowerCase();
    if (color === 'white' || color === 'silver') return '#f0f0f0';
    if (color === 'black') return '#2d3748';
    if (color === 'red') return '#f56565';
    if (color === 'blue') return '#4299e1';
    if (color === 'green') return '#48bb78';
    if (color === 'yellow') return '#ecc94b';
    if (color === 'pink') return '#ed64a6';
    if (color === 'purple') return '#9f7aea';
    if (color === 'orange') return '#ed8936';
    if (color === 'brown') return '#a0522d';
    if (color === 'gray' || color === 'grey') return '#a0aec0';
    // Default fallback
    return props.color || '#e2e8f0';
  }};
  cursor: pointer;
  transition: all 0.2s ease;
  
  &:hover {
    transform: scale(1.1);
    border-color: #667eea;
  }
`;

const AddToCartButton = styled.button`
  width: 100%;
  padding: 12px;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  border: none;
  border-radius: 8px;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
  margin-top: auto;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
  
  &:hover {
    background: linear-gradient(135deg, #5a6fd8 0%, #6a4190 100%);
    transform: translateY(-2px);
    box-shadow: 0 4px 12px rgba(102, 126, 234, 0.3);
  }
  
  &:active {
    transform: translateY(0);
  }
  
  &:disabled {
    background: #a0aec0;
    cursor: not-allowed;
    transform: none;
    box-shadow: none;
  }
`;

const SuccessMessage = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background: rgba(72, 187, 120, 0.95);
  color: white;
  padding: 8px 16px;
  border-radius: 20px;
  font-size: 12px;
  font-weight: 600;
  z-index: 10;
  animation: fadeInOut 2s ease-in-out;
  
  @keyframes fadeInOut {
    0% { opacity: 0; transform: translate(-50%, -50%) scale(0.8); }
    20% { opacity: 1; transform: translate(-50%, -50%) scale(1); }
    80% { opacity: 1; transform: translate(-50%, -50%) scale(1); }
    100% { opacity: 0; transform: translate(-50%, -50%) scale(0.8); }
  }
`;

const StockBadge = styled.div`
  position: absolute;
  top: 15px;
  left: 15px;
  background: ${props => props.inStock ? '#48bb78' : '#f56565'};
  color: white;
  padding: 4px 8px;
  border-radius: 12px;
  font-size: 10px;
  font-weight: 600;
  z-index: 2;
`;

const Product = ({ item }) => {
  const navigate = useNavigate();
  const [selectedSize, setSelectedSize] = useState('');
  const [selectedColor, setSelectedColor] = useState('');
  const [currentImage, setCurrentImage] = useState('');
  const [isAdding, setIsAdding] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);
  const [hasStock, setHasStock] = useState(true);
  const [availableColors, setAvailableColors] = useState([]);
  const [availableSizes, setAvailableSizes] = useState([]);

  // Initialize selections and image
  useEffect(() => {
    if (item) {
      // Extract available colors and sizes from inventory
      const colors = extractAvailableColors(item);
      const sizes = extractAvailableSizes(item);
      
      setAvailableColors(colors);
      setAvailableSizes(sizes);
      
      // Set default color (first available color)
      const defaultColor = colors.length > 0 ? colors[0] : '';
      setSelectedColor(defaultColor);
      
      // Set default size based on selected color
      const defaultSize = getSizesForColor(item, defaultColor);
      setSelectedSize(defaultSize.length > 0 ? defaultSize[0] : '');
      
      // Set initial image
      updateProductImage(defaultColor);
      
      // Check stock availability
      checkStockAvailability(defaultColor, defaultSize[0] || '');
    }
  }, [item]);

  // Extract available colors from inventory
  const extractAvailableColors = (product) => {
    if (!product.inventory || Object.keys(product.inventory).length === 0) {
      // Fallback to product.color if no inventory
      return product.color && Array.isArray(product.color) ? product.color : [];
    }
    
    // Get colors from inventory keys
    return Object.keys(product.inventory).filter(color => 
      Object.keys(product.inventory[color]).length > 0
    );
  };

  // Extract available sizes from inventory
  const extractAvailableSizes = (product) => {
    if (!product.inventory || Object.keys(product.inventory).length === 0) {
      // Fallback to product.size if no inventory
      return product.size && Array.isArray(product.size) ? product.size : [];
    }
    
    // Get all unique sizes from inventory
    const allSizes = new Set();
    Object.values(product.inventory).forEach(colorData => {
      Object.keys(colorData).forEach(size => {
        allSizes.add(size);
      });
    });
    
    return Array.from(allSizes);
  };

  // Get available sizes for a specific color
  const getSizesForColor = (product, color) => {
    if (!product.inventory || !product.inventory[color]) {
      return product.size && Array.isArray(product.size) ? product.size : [];
    }
    
    return Object.keys(product.inventory[color]).filter(size => 
      product.inventory[color][size] && product.inventory[color][size].quantity > 0
    );
  };

  // Update image when color changes
  useEffect(() => {
    if (selectedColor) {
      updateProductImage(selectedColor);
      
      // Update available sizes for selected color
      const sizesForColor = getSizesForColor(item, selectedColor);
      if (sizesForColor.length > 0 && !sizesForColor.includes(selectedSize)) {
        setSelectedSize(sizesForColor[0]);
      }
      
      checkStockAvailability(selectedColor, selectedSize);
    }
  }, [selectedColor, selectedSize]);

  const updateProductImage = (color) => {
    if (item.colorImages && item.colorImages[color]) {
      // Use color-specific image
      setCurrentImage(item.colorImages[color]);
    } else {
      // Fallback to main product image
      setCurrentImage(item.img);
    }
  };

  const checkStockAvailability = (color, size) => {
    if (!item.inventory || Object.keys(item.inventory).length === 0) {
      setHasStock(item.inStock !== false);
      return;
    }

    // Check if selected color-size combination has stock
    const inventoryItem = item.inventory[color]?.[size];
    const hasStockForSelection = inventoryItem && inventoryItem.quantity > 0;
    
    setHasStock(hasStockForSelection);
  };

  const handleColorSelect = (color) => {
    setSelectedColor(color);
  };

  const handleSizeSelect = (size) => {
    setSelectedSize(size);
    checkStockAvailability(selectedColor, size);
  };

  // Navigate to single product page
  const handleProductClick = () => {
    navigate(`/product/${item.id}`);
  };

  const addToCart = async (e) => {
    if (e) e.stopPropagation(); // Prevent navigating to product page
    
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login to add items to cart');
      return;
    }

    if (!hasStock) {
      alert('Selected item is out of stock');
      return;
    }

    setIsAdding(true);

    try {
      const base64Payload = token.split('.')[1];
      const payload = JSON.parse(atob(base64Payload));
      const userId = payload.userId;

      // Get the price for selected color/size combination
      const selectedPrice = item.inventory?.[selectedColor]?.[selectedSize]?.price || item.price;

      const cartItem = {
        productId: item.id,
        quantity: 1,
        price: selectedPrice,
        title: item.title,
        img: currentImage, // Use current image (color-specific)
        size: selectedSize,
        color: selectedColor
      };

      await axios.post(
        `${process.env.REACT_APP_API_URL}/api/cart/${userId}`,
        cartItem,
        { 
          headers: { 
            Authorization: `Bearer ${token}` 
          } 
        }
      );
      
      // Show success animation instead of alert
      setShowSuccess(true);
      setTimeout(() => setShowSuccess(false), 2000);
      
    } catch (error) {
      console.error('Error adding to cart:', error);
      alert('Failed to add product to cart');
    } finally {
      setIsAdding(false);
    }
  };

  // Get current price for selected color/size
  const getCurrentPrice = () => {
    if (item.inventory && selectedColor && selectedSize) {
      const inventoryPrice = item.inventory[selectedColor]?.[selectedSize]?.price;
      return inventoryPrice || item.price;
    }
    return item.price;
  };

  // Use current image or fallback
  const displayImage = currentImage || item.img || `https://via.placeholder.com/300x300/4A90E2/FFFFFF?text=${encodeURIComponent(item.title || 'Product')}`;
  
  // Get available sizes for current color
  const currentSizes = selectedColor ? getSizesForColor(item, selectedColor) : availableSizes;
  
  return (
    <Container onClick={handleProductClick}>
      <ImageContainer>
        <Image 
          src={displayImage} 
          alt={item.title}
          onError={(e) => {
            e.target.src = `https://via.placeholder.com/300x300/4A90E2/FFFFFF?text=${encodeURIComponent(item.title || 'Product')}`;
          }}
        />
        
        <WishlistIcon onClick={(e) => e.stopPropagation()}>
          <i className="far fa-heart"></i>
        </WishlistIcon>
        
        <StockBadge inStock={hasStock}>
          {hasStock ? 'In Stock' : 'Out of Stock'}
        </StockBadge>
        
        {showSuccess && (
          <SuccessMessage>
            <i className="fas fa-check" style={{ marginRight: '5px' }}></i>
            Added to Cart!
          </SuccessMessage>
        )}
      </ImageContainer>
      
      {/* PRODUCT DETAILS WITH SIZE & COLOR SELECTION */}
      <ProductDetails onClick={(e) => e.stopPropagation()}>
        <ProductTitle>{item.title}</ProductTitle>
        <ProductPrice>${getCurrentPrice()}</ProductPrice>
        
        {/* SIZE SELECTION */}
        {currentSizes && currentSizes.length > 0 && (
          <SelectionContainer>
            <SelectLabel>Size</SelectLabel>
            <Select 
              value={selectedSize} 
              onChange={(e) => handleSizeSelect(e.target.value)}
              onClick={(e) => e.stopPropagation()}
            >
              {currentSizes.map((size) => (
                <option key={size} value={size}>{size}</option>
              ))}
            </Select>
          </SelectionContainer>
        )}
        
        {/* COLOR SELECTION WITH THUMBNAILS */}
        {availableColors && availableColors.length > 0 && (
          <SelectionContainer>
            <SelectLabel>Color</SelectLabel>
            <ColorThumbnails>
              {availableColors.map((color) => (
                <ColorThumbnail
                  key={color}
                  color={color}
                  selected={selectedColor === color}
                  onClick={(e) => {
                    e.stopPropagation();
                    handleColorSelect(color);
                  }}
                  title={color}
                />
              ))}
            </ColorThumbnails>
          </SelectionContainer>
        )}
        
        {/* ADD TO CART BUTTON */}
        <AddToCartButton 
          onClick={addToCart} 
          disabled={isAdding || !hasStock}
        >
          {isAdding ? (
            <>
              <i className="fas fa-spinner fa-spin"></i>
              Adding...
            </>
          ) : !hasStock ? (
            <>
              <i className="fas fa-times"></i>
              Out of Stock
            </>
          ) : (
            <>
              <i className="fas fa-shopping-cart"></i>
              Add to Cart
            </>
          )}
        </AddToCartButton>
      </ProductDetails>
    </Container>
  );
};

export default Product;
