import React from 'react';
import {
  Container,
  Wrapper,
  Title,
  Top,
  TopButton,
  TopTexts,
  TopText,
  Bottom,
  Info,
  Summary,
  SummaryTitle,
  SummaryItem,
  SummaryItemText,
  SummaryItemPrice,
  Button,
  EmptyCart
} from './styles/CartPage.styles';
import Cart from '../components/cart/Cart';
import { useCart } from '../context/CartContext';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const CartPage = () => {
  const { cartItems, getCartTotal } = useCart();
  const { user } = useAuth();
  const navigate = useNavigate();

  const handleCheckout = () => {
    if (!user) {
      navigate('/login');
      return;
    }
    navigate('/checkout');
  };

  const subtotal = getCartTotal();
  const shipping = subtotal > 50 ? 0 : 10;
  const tax = subtotal * 0.08;
  const total = subtotal + shipping + tax;

  return (
    <Container>
      <Wrapper>
        <Title>SHOPPING CART</Title>
        <Top>
          <TopButton onClick={() => navigate('/')}>CONTINUE SHOPPING</TopButton>
          <TopTexts>
            <TopText>Shopping Bag ({cartItems.length})</TopText>
            <TopText>Your Wishlist (0)</TopText>
          </TopTexts>
          <TopButton filled onClick={handleCheckout}>
            CHECKOUT NOW
          </TopButton>
        </Top>
        
        {cartItems.length === 0 ? (
          <EmptyCart>
            <h2>Your cart is empty</h2>
            <p>Add some products to get started!</p>
            <TopButton onClick={() => navigate('/')}>START SHOPPING</TopButton>
          </EmptyCart>
        ) : (
          <Bottom>
            <Info>
              <Cart />
            </Info>
            <Summary>
              <SummaryTitle>ORDER SUMMARY</SummaryTitle>
              <SummaryItem>
                <SummaryItemText>Subtotal</SummaryItemText>
                <SummaryItemPrice>${subtotal.toFixed(2)}</SummaryItemPrice>
              </SummaryItem>
              <SummaryItem>
                <SummaryItemText>Estimated Shipping</SummaryItemText>
                <SummaryItemPrice>{shipping === 0 ? 'FREE' : `$${shipping.toFixed(2)}`}</SummaryItemPrice>
              </SummaryItem>
              <SummaryItem>
                <SummaryItemText>Tax</SummaryItemText>
                <SummaryItemPrice>${tax.toFixed(2)}</SummaryItemPrice>
              </SummaryItem>
              <SummaryItem type="total">
                <SummaryItemText>Total</SummaryItemText>
                <SummaryItemPrice>${total.toFixed(2)}</SummaryItemPrice>
              </SummaryItem>
              <Button onClick={handleCheckout}>
                {user ? 'PROCEED TO CHECKOUT' : 'SIGN IN TO CHECKOUT'}
              </Button>
            </Summary>
          </Bottom>
        )}
      </Wrapper>
    </Container>
  );
};

export default CartPage;
