import React, { useState, useEffect } from "react";
import styled from "styled-components";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Container = styled.div`
  flex: 1;
  margin: 15px;
  min-width: 280px;
  max-width: 320px;
  height: 480px;
  display: flex;
  flex-direction: column;
  align-items: center;
  background: white;
  position: relative;
  padding: 0;
  box-shadow: 0 4px 15px rgba(0,0,0,0.1);
  border-radius: 12px;
  cursor: pointer;
  transition: all 0.3s ease;
  border: 1px solid #f0f0f0;
  
  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 25px rgba(0,0,0,0.15);
  }
  
  @media (max-width: 768px) {
    min-width: 250px;
    max-width: 300px;
    height: 450px;
    margin: 10px;
  }
  
  @media (max-width: 480px) {
    min-width: 100%;
    max-width: 100%;
    height: auto;
    min-height: 400px;
    margin: 5px 0;
  }
`;

const ImageContainer = styled.div`
  position: relative;
  width: 100%;
  height: 250px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
  border-radius: 12px 12px 0 0;
  overflow: hidden;
  
  @media (max-width: 480px) {
    height: 200px;
  }
`;

const Image = styled.img`
  height: 200px;
  max-width: 85%;
  object-fit: contain;
  transition: transform 0.3s ease;
  
  ${Container}:hover & {
    transform: scale(1.05);
  }
  
  @media (max-width: 480px) {
    height: 160px;
  }
`;

const WishlistIcon = styled.div`
  position: absolute;
  top: 15px;
  right: 15px;
  width: 36px;
  height: 36px;
  border-radius: 50%;
  background: white;
  display: flex;
  align-items: center;
  justify-content: center;
  box-shadow: 0 2px 8px rgba(0,0,0,0.15);
  cursor: pointer;
  transition: all 0.3s ease;
  z-index: 2;
  
  &:hover {
    background: #ff4757;
    color: white;
    transform: scale(1.1);
  }
  
  @media (max-width: 480px) {
    width: 32px;
    height: 32px;
    top: 10px;
    right: 10px;
  }
`;

const ProductDetails = styled.div`
  width: 100%;
  padding: 20px;
  text-align: left;
  background: white;
  border-radius: 0 0 12px 12px;
  flex-grow: 1;
  display: flex;
  flex-direction: column;
  
  @media (max-width: 480px) {
    padding: 15px;
  }
`;

const ProductTitle = styled.h3`
  font-size: 16px;
  font-weight: 600;
  margin: 0 0 8px 0;
  color: #2d3748;
  line-height: 1.4;
  height: 45px;
  overflow: hidden;
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  
  @media (max-width: 480px) {
    font-size: 14px;
    height: 40px;
  }
`;

const ProductPrice = styled.p`
  font-size: 20px;
  font-weight: bold;
  color: #2d3748;
  margin: 0 0 15px 0;
  
  @media (max-width: 480px) {
    font-size: 18px;
    margin: 0 0 12px 0;
  }
`;

const SelectionContainer = styled.div`
  width: 100%;
  margin: 8px 0;
  
  @media (max-width: 480px) {
    margin: 6px 0;
  }
`;

const SelectLabel = styled.label`
  display: block;
  font-size: 12px;
  font-weight: 600;
  color: #718096;
  margin-bottom: 5px;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  
  @media (max-width: 480px) {
    font-size: 11px;
  }
`;

const Select = styled.select`
  width: 100%;
  padding: 10px 12px;
  border: 2px solid #e2e8f0;
  border-radius: 8px;
  font-size: 14px;
  background: white;
  transition: all 0.3s ease;
  
  &:focus {
    outline: none;
    border-color: #4299e1;
    box-shadow: 0 0 0 3px rgba(66, 153, 225, 0.1);
  }
  
  &:hover {
    border-color: #cbd5e0;
  }
  
  @media (max-width: 480px) {
    padding: 8px 10px;
    font-size: 13px;
  }
`;

const ColorThumbnails = styled.div`
  display: flex;
  gap: 6px;
  margin-top: 5px;
  flex-wrap: wrap;
`;

const ColorThumbnail = styled.div`
  width: 24px;
  height: 24px;
  border-radius: 50%;
  border: 2px solid ${props => props.selected ? '#667eea' : '#e2e8f0'};
  background: ${props => {
    const color = props.color?.toLowerCase();
    if (color === 'white' || color === 'silver') return '#f0f0f0';
    if (color === 'black') return '#2d3748';
    if (color === 'red') return '#f56565';
    if (color === 'blue') return '#4299e1';
    if (color === 'green') return '#48bb78';
    if (color === 'yellow') return '#ecc94b';
    if (color === 'pink') return '#ed64a6';
    if (color === 'purple') return '#9f7aea';
    if (color === 'orange') return '#ed8936';
    if (color === 'brown') return '#a0522d';
    if (color === 'gray' || color === 'grey') return '#a0aec0';
    return props.color || '#e2e8f0';
  }};
  cursor: pointer;
  transition: all 0.2s ease;
  
  &:hover {
    transform: scale(1.1);
    border-color: #667eea;
  }
  
  @media (max-width: 480px) {
    width: 22px;
    height: 22px;
  }
`;

const AddToCartButton = styled.button`
  width: 100%;
  padding: 12px;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  border: none;
  border-radius: 8px;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
  margin-top: auto;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
  min-height: 44px;
  
  &:hover {
    background: linear-gradient(135deg, #5a6fd8 0%, #6a4190 100%);
    transform: translateY(-2px);
    box-shadow: 0 4px 12px rgba(102, 126, 234, 0.3);
  }
  
  &:active {
    transform: translateY(0);
  }
  
  &:disabled {
    background: #a0aec0;
    cursor: not-allowed;
    transform: none;
    box-shadow: none;
  }
  
  @media (max-width: 480px) {
    padding: 10px;
    font-size: 13px;
    min-height: 42px;
  }
`;

const SuccessMessage = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background: rgba(72, 187, 120, 0.95);
  color: white;
  padding: 8px 16px;
  border-radius: 20px;
  font-size: 12px;
  font-weight: 600;
  z-index: 10;
  animation: fadeInOut 2s ease-in-out;
  
  @keyframes fadeInOut {
    0% { opacity: 0; transform: translate(-50%, -50%) scale(0.8); }
    20% { opacity: 1; transform: translate(-50%, -50%) scale(1); }
    80% { opacity: 1; transform: translate(-50%, -50%) scale(1); }
    100% { opacity: 0; transform: translate(-50%, -50%) scale(0.8); }
  }
  
  @media (max-width: 480px) {
    font-size: 11px;
    padding: 6px 12px;
  }
`;

const StockBadge = styled.div`
  position: absolute;
  top: 15px;
  left: 15px;
  background: ${props => props.inStock ? '#48bb78' : '#f56565'};
  color: white;
  padding: 4px 8px;
  border-radius: 12px;
  font-size: 10px;
  font-weight: 600;
  z-index: 2;
  
  @media (max-width: 480px) {
    top: 10px;
    left: 10px;
    font-size: 9px;
    padding: 3px 6px;
  }
`;

// ... (rest of your Product component logic remains the same)
const Product = ({ item }) => {
  const navigate = useNavigate();
  const [selectedSize, setSelectedSize] = useState('');
  const [selectedColor, setSelectedColor] = useState('');
  const [currentImage, setCurrentImage] = useState('');
  const [isAdding, setIsAdding] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);
  const [hasStock, setHasStock] = useState(true);
  const [availableColors, setAvailableColors] = useState([]);
  const [availableSizes, setAvailableSizes] = useState([]);

  // ... (rest of your useEffect and functions remain the same)

  return (
    <Container onClick={handleProductClick}>
      <ImageContainer>
        <Image 
          src={displayImage} 
          alt={item.title}
          onError={(e) => {
            e.target.src = `https://via.placeholder.com/300x300/4A90E2/FFFFFF?text=${encodeURIComponent(item.title || 'Product')}`;
          }}
        />
        
        <WishlistIcon onClick={(e) => e.stopPropagation()}>
          <i className="far fa-heart"></i>
        </WishlistIcon>
        
        <StockBadge inStock={hasStock}>
          {hasStock ? 'In Stock' : 'Out of Stock'}
        </StockBadge>
        
        {showSuccess && (
          <SuccessMessage>
            <i className="fas fa-check" style={{ marginRight: '5px' }}></i>
            Added to Cart!
          </SuccessMessage>
        )}
      </ImageContainer>
      
      <ProductDetails onClick={(e) => e.stopPropagation()}>
        <ProductTitle>{item.title}</ProductTitle>
        <ProductPrice>${getCurrentPrice()}</ProductPrice>
        
        {currentSizes && currentSizes.length > 0 && (
          <SelectionContainer>
            <SelectLabel>Size</SelectLabel>
            <Select 
              value={selectedSize} 
              onChange={(e) => handleSizeSelect(e.target.value)}
              onClick={(e) => e.stopPropagation()}
            >
              {currentSizes.map((size) => (
                <option key={size} value={size}>{size}</option>
              ))}
            </Select>
          </SelectionContainer>
        )}
        
        {availableColors && availableColors.length > 0 && (
          <SelectionContainer>
            <SelectLabel>Color</SelectLabel>
            <ColorThumbnails>
              {availableColors.map((color) => (
                <ColorThumbnail
                  key={color}
                  color={color}
                  selected={selectedColor === color}
                  onClick={(e) => {
                    e.stopPropagation();
                    handleColorSelect(color);
                  }}
                  title={color}
                />
              ))}
            </ColorThumbnails>
          </SelectionContainer>
        )}
        
        <AddToCartButton 
          onClick={addToCart} 
          disabled={isAdding || !hasStock}
        >
          {isAdding ? (
            <>
              <i className="fas fa-spinner fa-spin"></i>
              Adding...
            </>
          ) : !hasStock ? (
            <>
              <i className="fas fa-times"></i>
              Out of Stock
            </>
          ) : (
            <>
              <i className="fas fa-shopping-cart"></i>
              Add to Cart
            </>
          )}
        </AddToCartButton>
      </ProductDetails>
    </Container>
  );
};

export default Product;
