import React, { useState } from 'react';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import styled from 'styled-components';

const PaymentFormContainer = styled.div`
  margin: 20px 0;
`;

const CardElementContainer = styled.div`
  padding: 12px;
  border: 1px solid #ddd;
  border-radius: 4px;
  margin: 10px 0;
  background: white;
`;

const PayButton = styled.button`
  width: 100%;
  padding: 12px;
  background-color: #4CAF50;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  font-weight: 600;
  margin-top: 15px;
  
  &:disabled {
    background-color: #cccccc;
    cursor: not-allowed;
  }
`;

const ErrorMessage = styled.div`
  color: #f44336;
  background-color: #ffebee;
  padding: 10px;
  border-radius: 4px;
  margin: 10px 0;
  font-size: 14px;
`;

const LoadingMessage = styled.div`
  color: #666;
  text-align: center;
  padding: 20px;
`;

const StripePayment = ({ clientSecret, amount, onSuccess, onError }) => {
  const stripe = useStripe();
  const elements = useElements();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    
    if (!stripe || !elements) {
      return;
    }

    setLoading(true);
    setError('');

    const cardElement = elements.getElement(CardElement);

    try {
      const { error: stripeError, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: {
          card: cardElement,
        }
      });

      if (stripeError) {
        setError(stripeError.message);
        onError(stripeError.message);
      } else if (paymentIntent.status === 'succeeded') {
        onSuccess(paymentIntent);
      }
    } catch (err) {
      setError('Payment failed. Please try again.');
      onError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const cardElementOptions = {
    style: {
      base: {
        fontSize: '16px',
        color: '#424770',
        '::placeholder': {
          color: '#aab7c4',
        },
      },
      invalid: {
        color: '#9e2146',
      },
    },
    hidePostalCode: true,
  };

  return (
    <PaymentFormContainer>
      <h3>Enter Card Details</h3>
      <p>Total Amount: ${amount}</p>
      
      <form onSubmit={handleSubmit}>
        <CardElementContainer>
          <CardElement options={cardElementOptions} />
        </CardElementContainer>
        
        {error && <ErrorMessage>{error}</ErrorMessage>}
        
        <PayButton type="submit" disabled={!stripe || loading}>
          {loading ? 'Processing Payment...' : `Pay $${amount}`}
        </PayButton>
      </form>

      {/* Test Card Info */}
      <div style={{ marginTop: '15px', padding: '10px', background: '#f5f5f5', borderRadius: '4px', fontSize: '12px' }}>
        <strong>Test Card:</strong> 4242 4242 4242 4242<br/>
        <strong>Expiry:</strong> 12/34 <strong>CVC:</strong> 123 <strong>ZIP:</strong> 12345
      </div>
    </PaymentFormContainer>
  );
};

export default StripePayment;
