import styled from 'styled-components';

export const Container = styled.div`
  padding: 20px;
  background: #f8f9fa;
  min-height: 100vh;
`;

export const Title = styled.h1`
  text-align: center;
  margin-bottom: 40px;
  color: #333;
  font-size: 2.5rem;
  font-weight: 300;
`;

export const OrderList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 25px;
  max-width: 1000px;
  margin: 0 auto;
`;

export const OrderItem = styled.div`
  background: white;
  border-radius: 15px;
  box-shadow: 0 4px 15px rgba(0,0,0,0.1);
  overflow: hidden;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 25px rgba(0,0,0,0.15);
  }
`;

export const OrderHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 25px 30px;
  background: #f8f9fa;
  border-bottom: 1px solid #eee;

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 15px;
    align-items: flex-start;
  }
`;

export const OrderInfo = styled.div`
  h3 {
    margin: 0 0 8px 0;
    color: teal;
    font-size: 1.3rem;
  }

  p {
    margin: 4px 0;
    color: #666;
    font-size: 0.9rem;
  }
`;

export const OrderStatus = styled.span`
  padding: 8px 16px;
  border-radius: 20px;
  font-size: 0.8rem;
  font-weight: 600;
  text-transform: uppercase;
  background-color: ${props => {
    switch (props.status) {
      case 'delivered': return '#e8f5e9';
      case 'shipped': return '#e3f2fd';
      case 'processing': return '#fff3e0';
      case 'cancelled': return '#ffebee';
      default: return '#f5f5f5';
    }
  }};
  color: ${props => {
    switch (props.status) {
      case 'delivered': return '#2e7d32';
      case 'shipped': return '#1565c0';
      case 'processing': return '#ef6c00';
      case 'cancelled': return '#d32f2f';
      default: return '#666';
    }
  }};
`;

export const ProductList = styled.div`
  padding: 0;
`;

export const ProductItem = styled.div`
  display: flex;
  align-items: center;
  padding: 20px 30px;
  border-bottom: 1px solid #f0f0f0;
  gap: 20px;

  &:last-child {
    border-bottom: none;
  }

  @media (max-width: 768px) {
    flex-direction: column;
    text-align: center;
    gap: 15px;
  }
`;

export const ProductImage = styled.img`
  width: 80px;
  height: 80px;
  object-fit: cover;
  border-radius: 8px;
  border: 1px solid #eee;
`;

export const ProductDetails = styled.div`
  flex: 1;

  h4 {
    margin: 0 0 8px 0;
    color: #333;
    font-size: 1.1rem;
  }

  p {
    margin: 4px 0;
    color: #666;
    font-size: 0.9rem;
  }
`;

export const ProductPrice = styled.div`
  text-align: right;
  font-weight: 600;
  color: #333;
  font-size: 1.1rem;

  @media (max-width: 768px) {
    text-align: center;
  }
`;

export const EmptyState = styled.div`
  text-align: center;
  padding: 80px 20px;
  color: #666;

  h2 {
    margin-bottom: 20px;
    color: #333;
    font-weight: 300;
  }

  p {
    margin-bottom: 30px;
    font-size: 1.1rem;
  }
`;

export const Loading = styled.div`
  text-align: center;
  padding: 60px 20px;
  font-size: 1.2rem;
  color: #666;
`;

export const Error = styled.div`
  text-align: center;
  padding: 40px 20px;
  background: #ffebee;
  color: #d32f2f;
  border-radius: 10px;
  margin: 20px 0;
`;
