import React, { useState, useEffect } from "react";
import styled from "styled-components";
import Slider from "../components/Slider";
import Announcement from "../components/Announcement";
import Categories from "../components/Categories";
import Products from "../components/Products";
import Newsletter from "../components/Newsletter";
import axios from "axios";

const Container = styled.div`
  width: 100vw;
  overflow-x: hidden;
`;

const LoadingMessage = styled.div`
  text-align: center;
  padding: 50px;
  font-size: 18px;
`;

const ErrorMessage = styled.div`
  text-align: center;
  padding: 50px;
  font-size: 18px;
  color: red;
`;

const Home = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        setError("");
        console.log('Fetching products from:', `${process.env.REACT_APP_API_URL}/api/products`);
        
        const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/products`);
        console.log('Products response:', response.data);
        
        setProducts(response.data.slice(0, 8)); // Show first 8 products
      } catch (error) {
        console.error("Error fetching products:", error);
        setError("Failed to load products. Please check if the product service is running.");
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  if (loading) {
    return (
      <Container>
        <Announcement />
        <Slider />
        <Categories />
        <LoadingMessage>Loading products...</LoadingMessage>
        <Newsletter />
      </Container>
    );
  }

  if (error) {
    return (
      <Container>
        <Announcement />
        <Slider />
        <Categories />
        <ErrorMessage>{error}</ErrorMessage>
        <Newsletter />
      </Container>
    );
  }

  return (
    <Container>
      <Announcement />
      <Slider />
      <Categories />
      <Products products={products} loading={loading} />
      <Newsletter />
    </Container>
  );
};

export default Home;
