import React from "react";
import styled from "styled-components";
import Product from "./Product";

const Container = styled.div`
  padding: 20px;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  gap: 25px;
  max-width: 1400px;
  margin: 0 auto;
  
  @media (max-width: 768px) {
    padding: 15px;
    gap: 15px;
    justify-content: space-around;
  }
  
  @media (max-width: 480px) {
    padding: 10px;
    gap: 10px;
    justify-content: center;
  }
`;

const ProductsGrid = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  gap: 25px;
  width: 100%;
  
  @media (max-width: 768px) {
    gap: 15px;
  }
  
  @media (max-width: 480px) {
    gap: 10px;
  }
`;

const Products = ({ products, loading }) => {
  if (loading) {
    return (
      <Container>
        <div style={{ 
          textAlign: 'center', 
          width: '100%', 
          padding: '40px 20px', 
          color: '#718096' 
        }}>
          <i className="fas fa-spinner fa-spin" style={{ fontSize: '1.5rem', marginBottom: '15px', display: 'block' }}></i>
          Loading products...
        </div>
      </Container>
    );
  }

  return (
    <Container>
      <ProductsGrid>
        {products.map((item) => (
          <Product item={item} key={item.id} />
        ))}
      </ProductsGrid>
    </Container>
  );
};

export default Products;
