import React from "react";
import styled from "styled-components";
import axios from "axios";

const Info = styled.div`
  opacity: 0;
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  left: 0;
  background-color: rgba(0, 0, 0, 0.2);
  z-index: 3;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.5s ease;
  cursor: pointer;
`;

const Container = styled.div`
  flex: 1;
  margin: 5px;
  min-width: 280px;
  height: 420px; /* Increased height to fit product details */
  display: flex;
  flex-direction: column;
  align-items: center;
  background-color: #f5fbfd;
  position: relative;
  padding: 10px;
  box-shadow: 0 2px 8px rgba(0,0,0,0.1);
  border-radius: 8px;
  
  &:hover ${Info} {
    opacity: 1;
  }
`;

const ProductDetails = styled.div`
  width: 100%;
  padding: 10px;
  text-align: center;
  z-index: 4;
  position: relative;
  background: white;
  border-radius: 0 0 8px 8px;
`;

const ProductTitle = styled.h3`
  font-size: 16px;
  font-weight: 600;
  margin: 8px 0;
  color: #333;
  height: 40px;
  overflow: hidden;
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
`;

const ProductPrice = styled.p`
  font-size: 18px;
  font-weight: bold;
  color: #2874f0;
  margin: 5px 0;
`;

const ProductSize = styled.p`
  font-size: 14px;
  color: #666;
  margin: 3px 0;
`;

const ProductColor = styled.p`
  font-size: 14px;
  color: #666;
  margin: 3px 0;
`;

const ImageContainer = styled.div`
  position: relative;
  width: 100%;
  height: 250px;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: #f5fbfd;
  border-radius: 8px 8px 0 0;
`;

const Circle = styled.div`
  width: 180px;
  height: 180px;
  border-radius: 50%;
  background-color: white;
  position: absolute;
`;

const Image = styled.img`
  height: 200px;
  max-width: 90%;
  object-fit: contain;
  z-index: 2;
`;

const Icon = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background-color: white;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 10px;
  transition: all 0.5s ease;
  cursor: pointer;
  
  &:hover {
    background-color: #e9f5f5;
    transform: scale(1.1);
  }
`;

const Product = ({ item }) => {
  console.log('Product data:', item);
  
  const addToCart = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login to add items to cart');
      return;
    }

    try {
      const base64Payload = token.split('.')[1];
      const payload = JSON.parse(atob(base64Payload));
      const userId = payload.userId;

      const cartItem = {
        productId: item.id,
        quantity: 1,
        price: item.price,
        title: item.title,
        img: item.img
      };

      await axios.post(
        `${process.env.REACT_APP_API_URL}/api/cart/${userId}`,
        cartItem,
        { 
          headers: { 
            Authorization: `Bearer ${token}` 
          } 
        }
      );
      
      alert('Product added to cart!');
    } catch (error) {
      console.error('Error adding to cart:', error);
      alert('Failed to add product to cart');
    }
  };

  // Use product image or fallback
  const imageUrl = item.img || `https://via.placeholder.com/300x300/4A90E2/FFFFFF?text=${encodeURIComponent(item.title || 'Product')}`;
  
  return (
    <Container>
      <ImageContainer>
        <Circle />
        <Image 
          src={imageUrl} 
          alt={item.title}
          onError={(e) => {
            e.target.src = `https://via.placeholder.com/300x300/4A90E2/FFFFFF?text=${encodeURIComponent(item.title || 'Product')}`;
          }}
        />
        <Info>
          <Icon onClick={addToCart}>
            <i className="fas fa-shopping-cart"></i>
          </Icon>
          <Icon>
            <i className="fas fa-search"></i>
          </Icon>
          <Icon>
            <i className="fas fa-heart"></i>
          </Icon>
        </Info>
      </ImageContainer>
      
      {/* ADDED PRODUCT DETAILS SECTION */}
      <ProductDetails>
        <ProductTitle>{item.title}</ProductTitle>
        <ProductPrice>${item.price}</ProductPrice>
        <ProductSize>
          <strong>Size:</strong> {item.size && item.size.join(', ')}
        </ProductSize>
        <ProductColor>
          <strong>Color:</strong> {item.color && item.color.join(', ')}
        </ProductColor>
      </ProductDetails>
    </Container>
  );
};

export default Product;
