import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

// ... (ALL YOUR STYLED COMPONENTS REMAIN EXACTLY THE SAME) ...

const AdminProducts = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [editingProduct, setEditingProduct] = useState(null);
  const [formLoading, setFormLoading] = useState(false);
  const [deleteLoading, setDeleteLoading] = useState(null);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const [productForm, setProductForm] = useState({
    title: '',
    desc: '',
    price: '',
    categories: '',
    size: '',
    color: '',
    inStock: true,
    img: null
  });

  // State for color images
  const [colorImages, setColorImages] = useState([]);
  const [mainImagePreview, setMainImagePreview] = useState('');

  // FIXED: Inventory state - now properly managed
  const [inventory, setInventory] = useState({});
  const [inventoryInitialized, setInventoryInitialized] = useState(false);

  const getAuthHeader = () => {
    const token = localStorage.getItem('adminToken');
    return token ? { Authorization: `Bearer ${token}` } : {};
  };

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const response = await axios.get(
        `${process.env.REACT_APP_API_URL}/api/products`,
        { headers: getAuthHeader() }
      );
      setProducts(response.data.products || response.data || []);
    } catch (error) {
      console.error('Error fetching products:', error);
      setProducts([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  // Color Images Functions (keep exactly as is)
  const handleBulkColorImages = (e) => {
    const files = Array.from(e.target.files);
    
    const newColorImages = files.map(file => {
      const colorName = file.name.split('.')[0];
      return {
        color: colorName,
        imageFile: file,
        imagePreview: URL.createObjectURL(file)
      };
    });

    setColorImages([...colorImages, ...newColorImages]);
  };

  const handleSingleColorImage = (index, file) => {
    const updated = [...colorImages];
    if (!updated[index]) {
      updated[index] = { color: '', imageFile: null, imagePreview: '' };
    }
    updated[index].imageFile = file;
    updated[index].imagePreview = URL.createObjectURL(file);
    
    if (!updated[index].color) {
      updated[index].color = file.name.split('.')[0];
    }
    
    setColorImages(updated);
  };

  const updateColor = (index, color) => {
    const updated = [...colorImages];
    if (!updated[index]) {
      updated[index] = { color: '', imageFile: null, imagePreview: '' };
    }
    updated[index].color = color;
    setColorImages(updated);
  };

  const removeColorImage = (index) => {
    setColorImages(colorImages.filter((_, i) => i !== index));
  };

  const addColorImageSlot = () => {
    setColorImages([...colorImages, { color: '', imageFile: null, imagePreview: '' }]);
  };

  // ✅ FIXED: Update Inventory Matrix - PRESERVE USER INPUT
  const updateInventory = (color, size, field, value) => {
    setInventory(prev => ({
      ...prev,
      [color]: {
        ...prev[color],
        [size]: {
          ...prev[color]?.[size],
          [field]: field === 'quantity' ? parseInt(value) || 0 : 
                   field === 'price' ? parseFloat(value) || productForm.price : value
        }
      }
    }));
  };

  // ✅ FIXED: Initialize inventory ONLY when colors/sizes are first set - NO AUTO-FILLING
  useEffect(() => {
    if (productForm.color && productForm.size && !inventoryInitialized) {
      const colors = productForm.color.split(',').map(c => c.trim()).filter(c => c);
      const sizes = productForm.size.split(',').map(s => s.trim()).filter(s => s);
      
      const newInventory = {};
      
      colors.forEach(color => {
        newInventory[color] = {};
        sizes.forEach(size => {
          // ✅ ONLY use existing values, don't create defaults
          const existingValue = inventory[color]?.[size];
          if (existingValue) {
            newInventory[color][size] = { ...existingValue };
          }
          // If no existing value, don't create a default entry
        });
      });
      
      setInventory(newInventory);
      setInventoryInitialized(true);
    }
  }, [productForm.color, productForm.size]);

  // ✅ FIXED: Reset inventory initialization when modal closes
  useEffect(() => {
    if (!showModal) {
      setInventoryInitialized(false);
    }
  }, [showModal]);

  const handleInputChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    
    if (type === 'file' && name === 'img') {
      setProductForm(prev => ({
        ...prev,
        img: files[0]
      }));
      if (files[0]) {
        setMainImagePreview(URL.createObjectURL(files[0]));
      }
    } else if (type === 'checkbox') {
      setProductForm(prev => ({
        ...prev,
        [name]: checked
      }));
    } else {
      setProductForm(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  // ✅ FIXED: PROPER INVENTORY HANDLING - ONLY SAVE WHAT USER EXPLICITLY SET
  const handleAddProduct = async (e) => {
    e.preventDefault();
    setFormLoading(true);
    setError('');

    try {
      if (!productForm.img) {
        setError('Please select a main product image');
        setFormLoading(false);
        return;
      }

      const formData = new FormData();
      formData.append('title', productForm.title);
      formData.append('desc', productForm.desc);
      formData.append('price', productForm.price);
      formData.append('categories', productForm.categories);
      formData.append('size', productForm.size);
      formData.append('color', productForm.color);
      formData.append('inStock', productForm.inStock);
      formData.append('img', productForm.img);

      // Color images
      colorImages.forEach((item, index) => {
        if (item.color && item.imageFile) {
          formData.append(`color_${index}`, item.color);
          formData.append(`colorImage_${index}`, item.imageFile);
        }
      });

      // ✅ FIXED: Only send inventory entries that have explicit values
      console.log('📦 Processing inventory data from user input...');
      
      const colors = productForm.color.split(',').map(c => c.trim()).filter(c => c);
      const sizes = productForm.size.split(',').map(s => s.trim()).filter(s => s);
      
      let inventoryToSend = {};
      
      if (colors.length > 0 && sizes.length > 0) {
        colors.forEach(color => {
          inventoryToSend[color] = {};
          sizes.forEach(size => {
            // ✅ ONLY include if user explicitly set a quantity
            const currentData = inventory[color]?.[size];
            if (currentData && currentData.quantity !== undefined) {
              inventoryToSend[color][size] = {
                quantity: currentData.quantity || 0,
                price: currentData.price || parseFloat(productForm.price) || 0
              };
            }
          });
          
          // Remove color entry if no sizes have values
          if (Object.keys(inventoryToSend[color]).length === 0) {
            delete inventoryToSend[color];
          }
        });
        
        console.log('✅ Inventory data prepared (only explicit values):', inventoryToSend);
      } else {
        console.log('⚠️ No colors or sizes available for inventory');
      }

      formData.append('inventory', JSON.stringify(inventoryToSend));

      await axios.post(
        `${process.env.REACT_APP_API_URL}/api/products/upload`,
        formData,
        {
          headers: {
            ...getAuthHeader(),
            'Content-Type': 'multipart/form-data'
          }
        }
      );

      setShowModal(false);
      resetForm();
      fetchProducts();
      alert('Product added successfully!');
    } catch (error) {
      console.error('❌ Error adding product:', error);
      console.error('❌ Error response:', error.response?.data);
      setError(`Failed to add product: ${error.response?.data?.message || error.message}`);
    } finally {
      setFormLoading(false);
    }
  };

  // ✅ FIXED: Edit product - properly load existing inventory WITHOUT AUTO-FILLING
  const handleEditProduct = (product) => {
    setEditingProduct(product);
    setProductForm({
      title: product.title,
      desc: product.desc || '',
      price: product.price,
      categories: product.categories ? product.categories.join(',') : '',
      size: product.size ? product.size.join(',') : '',
      color: product.color ? product.color.join(',') : '',
      inStock: product.inStock,
      img: null
    });
    setMainImagePreview(product.img || '');
    setColorImages([]);
    
    // ✅ FIXED: ONLY load existing inventory, don't auto-fill missing combinations
    if (product.inventory && Object.keys(product.inventory).length > 0) {
      console.log('📦 Loading existing inventory (no auto-fill):', product.inventory);
      setInventory(product.inventory);
    } else {
      // Create completely empty inventory - no auto-filling
      setInventory({});
    }
    
    setInventoryInitialized(true);
    setShowModal(true);
    setError('');
  };

  // ✅ FIXED: Update product - only save explicit user inputs
  const handleUpdateProduct = async (e) => {
    e.preventDefault();
    setFormLoading(true);
    setError('');

    try {
      const formData = new FormData();
      formData.append('title', productForm.title);
      formData.append('desc', productForm.desc);
      formData.append('price', productForm.price);
      formData.append('categories', productForm.categories);
      formData.append('size', productForm.size);
      formData.append('color', productForm.color);
      formData.append('inStock', productForm.inStock);
      
      // ✅ FIXED: Only save inventory entries that have explicit values
      const colors = productForm.color.split(',').map(c => c.trim()).filter(c => c);
      const sizes = productForm.size.split(',').map(s => s.trim()).filter(s => s);
      
      let inventoryToSend = {};
      if (colors.length > 0 && sizes.length > 0) {
        colors.forEach(color => {
          inventoryToSend[color] = {};
          sizes.forEach(size => {
            // ✅ ONLY include if user explicitly set a quantity
            const currentData = inventory[color]?.[size];
            if (currentData && currentData.quantity !== undefined) {
              inventoryToSend[color][size] = {
                quantity: currentData.quantity || 0,
                price: currentData.price || parseFloat(productForm.price) || 0
              };
            }
          });
          
          // Remove color entry if no sizes have values
          if (Object.keys(inventoryToSend[color]).length === 0) {
            delete inventoryToSend[color];
          }
        });
      }
      
      console.log('📦 Updating with inventory (only explicit values):', inventoryToSend);
      formData.append('inventory', JSON.stringify(inventoryToSend));
      
      if (productForm.img) {
        formData.append('img', productForm.img);
      }

      await axios.put(
        `${process.env.REACT_APP_API_URL}/api/products/${editingProduct.id}`,
        formData,
        {
          headers: {
            ...getAuthHeader(),
            'Content-Type': 'multipart/form-data'
          }
        }
      );

      setShowModal(false);
      setEditingProduct(null);
      resetForm();
      fetchProducts();
      alert('Product updated successfully!');
    } catch (error) {
      console.error('Error updating product:', error);
      setError(`Failed to update product: ${error.response?.data?.message || error.message}`);
    } finally {
      setFormLoading(false);
    }
  };

  const handleDeleteProduct = async (productId) => {
    if (!window.confirm('Are you sure you want to delete this product? All product images will also be deleted from storage.')) {
      return;
    }

    setDeleteLoading(productId);

    try {
      await axios.delete(
        `${process.env.REACT_APP_API_URL}/api/products/${productId}`,
        { headers: getAuthHeader() }
      );
      fetchProducts();
      alert('Product and all associated images deleted successfully!');
    } catch (error) {
      console.error('Error deleting product:', error);
      alert('Failed to delete product');
    } finally {
      setDeleteLoading(null);
    }
  };

  const resetForm = () => {
    setProductForm({
      title: '',
      desc: '',
      price: '',
      categories: '',
      size: '',
      color: '',
      inStock: true,
      img: null
    });
    setColorImages([]);
    setMainImagePreview('');
    setInventory({});
    setInventoryInitialized(false);
    setError('');
  };

  const openAddModal = () => {
    setEditingProduct(null);
    resetForm();
    setShowModal(true);
  };

  // Helper function to get inventory value for display
  const getInventoryValue = (color, size, field) => {
    return inventory[color]?.[size]?.[field] || (field === 'quantity' ? '' : productForm.price || '');
  };

  if (loading) {
    return (
      <Container>
        <Header>
          <h1>📦 Manage Products</h1>
        </Header>
        <Loading>Loading products from database...</Loading>
      </Container>
    );
  }

  return (
    <Container>
      <Header>
        <h1>📦 Manage Products</h1>
        <Button onClick={() => navigate('/admin/dashboard')}>
          ← Back to Dashboard
        </Button>
      </Header>

      <ActionBar>
        <Button onClick={openAddModal}>
          ➕ Add New Product
        </Button>
        <Button onClick={fetchProducts}>
          🔄 Refresh
        </Button>
      </ActionBar>

      {/* Add/Edit Product Modal */}
      {showModal && (
        <Modal>
          <ModalContent>
            <h2>{editingProduct ? 'Edit Product' : 'Add New Product'}</h2>
            
            {error && (
              <ErrorMessage>
                ❌ {error}
              </ErrorMessage>
            )}
            
            <Form onSubmit={editingProduct ? handleUpdateProduct : handleAddProduct}>
              <Input
                type="text"
                name="title"
                placeholder="Product Title"
                value={productForm.title}
                onChange={handleInputChange}
                required
                disabled={formLoading}
              />
              
              <TextArea
                name="desc"
                placeholder="Product Description"
                value={productForm.desc}
                onChange={handleInputChange}
                disabled={formLoading}
              />
              
              <Input
                type="number"
                name="price"
                placeholder="Base Price"
                value={productForm.price}
                onChange={handleInputChange}
                step="0.01"
                min="0"
                required
                disabled={formLoading}
              />
              
              <Input
                type="text"
                name="categories"
                placeholder="Categories (comma separated)"
                value={productForm.categories}
                onChange={handleInputChange}
                disabled={formLoading}
              />
              
              <Input
                type="text"
                name="size"
                placeholder="Sizes (comma separated) - S, M, L, XL"
                value={productForm.size}
                onChange={handleInputChange}
                disabled={formLoading}
              />
              
              <Input
                type="text"
                name="color"
                placeholder="Colors (comma separated) - Red, Blue, Black"
                value={productForm.color}
                onChange={handleInputChange}
                disabled={formLoading}
              />
              
              {/* Main Product Image */}
              <div>
                <label><strong>Main Product Image *</strong></label>
                <FileInput
                  type="file"
                  name="img"
                  accept="image/*"
                  onChange={handleInputChange}
                  disabled={formLoading}
                />
                {mainImagePreview && (
                  <div style={{marginTop: '10px'}}>
                    <ImagePreview src={mainImagePreview} alt="Main product preview" />
                  </div>
                )}
                <small>This image is required for product creation</small>
              </div>

              {/* Color-Specific Images Section */}
              <ColorImageSection>
                <label><strong>Color-Specific Images (Optional)</strong></label>
                
                <BulkUploadSection>
                  <label>Bulk Upload Color Images</label>
                  <FileInput
                    type="file"
                    accept="image/*"
                    multiple
                    onChange={handleBulkColorImages}
                    style={{ margin: '10px 0' }}
                    disabled={formLoading}
                  />
                  <small>Select multiple images at once. Color names will be taken from filenames.</small>
                </BulkUploadSection>

                <div>
                  <label>Color Images Preview</label>
                  <ColorImageGrid>
                    {colorImages.map((item, index) => (
                      <ColorImageCard key={index}>
                        <ColorNameInput
                          type="text"
                          placeholder="Color name"
                          value={item.color}
                          onChange={(e) => updateColor(index, e.target.value)}
                          disabled={formLoading}
                        />
                        {item.imagePreview ? (
                          <ImagePreview src={item.imagePreview} alt={`${item.color} preview`} />
                        ) : (
                          <FileInput
                            type="file"
                            accept="image/*"
                            onChange={(e) => handleSingleColorImage(index, e.target.files[0])}
                            style={{ fontSize: '12px', padding: '4px' }}
                            disabled={formLoading}
                          />
                        )}
                        <RemoveButton 
                          type="button" 
                          onClick={() => removeColorImage(index)}
                          disabled={formLoading}
                        >
                          Remove
                        </RemoveButton>
                      </ColorImageCard>
                    ))}
                  </ColorImageGrid>
                  
                  <AddButton 
                    type="button" 
                    onClick={addColorImageSlot}
                    disabled={formLoading}
                  >
                    + Add Single Color Image
                  </AddButton>
                </div>
              </ColorImageSection>

              {/* ✅ FIXED: Inventory Matrix Section - ONLY SHOW EXPLICIT VALUES */}
              {productForm.color && productForm.size && (
                <InventorySection>
                  <label><strong>Color-Size Inventory Management</strong></label>
                  <small style={{display: 'block', marginBottom: '15px', color: '#666'}}>
                    Set different quantities and prices for each color-size combination. 
                    {editingProduct ? ' Only previously set values are shown.' : ' Leave empty for sizes without inventory.'}
                  </small>
                  
                  {productForm.color.split(',').map(color => color.trim()).filter(color => color).map(color => (
                    <ColorSection key={color}>
                      <ColorHeader>
                        <ColorName>{color}</ColorName>
                        <ColorImagePreview>
                          {colorImages.find(img => img.color === color)?.imagePreview && (
                            <img 
                              src={colorImages.find(img => img.color === color).imagePreview} 
                              alt={color} 
                            />
                          )}
                        </ColorImagePreview>
                      </ColorHeader>
                      
                      <SizeGrid>
                        {productForm.size.split(',').map(size => size.trim()).filter(size => size).map(size => (
                          <SizeCell key={size}>
                            <SizeLabel>{size}</SizeLabel>
                            <MatrixLabel>Quantity</MatrixLabel>
                            <MatrixInput
                              type="number"
                              placeholder="0"
                              min="0"
                              value={getInventoryValue(color, size, 'quantity')}
                              onChange={(e) => updateInventory(color, size, 'quantity', e.target.value)}
                              disabled={formLoading}
                            />
                            <MatrixLabel>Price ($)</MatrixLabel>
                            <MatrixInput
                              type="number"
                              placeholder={productForm.price}
                              step="0.01"
                              min="0"
                              value={getInventoryValue(color, size, 'price')}
                              onChange={(e) => updateInventory(color, size, 'price', e.target.value)}
                              disabled={formLoading}
                            />
                          </SizeCell>
                        ))}
                      </SizeGrid>
                    </ColorSection>
                  ))}
                </InventorySection>
              )}
              
              <label>
                <input
                  type="checkbox"
                  name="inStock"
                  checked={productForm.inStock}
                  onChange={handleInputChange}
                  disabled={formLoading}
                />
                In Stock
              </label>
              
              <FormActions>
                <Button type="submit" disabled={formLoading}>
                  {formLoading ? 'Saving...' : (editingProduct ? 'Update Product' : 'Add Product')}
                </Button>
                <Button 
                  type="button" 
                  onClick={() => {
                    setShowModal(false);
                    setEditingProduct(null);
                    resetForm();
                  }}
                  style={{ background: '#6c757d' }}
                  disabled={formLoading}
                >
                  Cancel
                </Button>
              </FormActions>
            </Form>
          </ModalContent>
        </Modal>
      )}

      {/* Products Grid */}
      <ProductsGrid>
        {products.map((product) => (
          <ProductCard key={product.id}>
            <ProductImage 
              src={product.img || 'https://via.placeholder.com/300x300?text=No+Image'} 
              alt={product.title}
            />
            <ProductTitle>{product.title}</ProductTitle>
            <ProductPrice>${product.price}</ProductPrice>
            
            <ProductInfo>
              Categories: {product.categories ? product.categories.join(', ') : 'N/A'}
            </ProductInfo>
            
            <ProductInfo>
              Sizes: {product.size ? product.size.join(', ') : 'N/A'}
            </ProductInfo>
            
            <ProductInfo>
              Colors: {product.color ? product.color.join(', ') : 'N/A'}
            </ProductInfo>
            
            <ProductInfo>
              Status: <StockStatus className={product.inStock ? 'in-stock' : 'out-of-stock'}>
                {product.inStock ? 'In Stock' : 'Out of Stock'}
              </StockStatus>
            </ProductInfo>
            
            <ProductActions>
              <Button 
                onClick={() => handleEditProduct(product)}
                style={{background: '#28a745'}}
                disabled={deleteLoading === product.id}
              >
                ✏️ Edit
              </Button>
              <Button 
                onClick={() => handleDeleteProduct(product.id)}
                style={{background: '#dc3545'}}
                disabled={deleteLoading === product.id}
              >
                {deleteLoading === product.id ? (
                  <DeleteLoading>
                    <i className="fas fa-spinner fa-spin"></i>
                    Deleting...
                  </DeleteLoading>
                ) : (
                  '🗑️ Delete'
                )}
              </Button>
            </ProductActions>
          </ProductCard>
        ))}
      </ProductsGrid>
    </Container>
  );
};

export default AdminProducts;
