import React, { useState, useEffect } from 'react';
import { Container } from './styles/Categories.styles';
import CategoryItem from './CategoryItem';
import { productService } from '../services/productService';

const Categories = () => {
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        // If your API has categories endpoint, use it
        // Otherwise, you can get categories from products or use static categories
        const products = await productService.getAllProducts();
        
        // Extract unique categories from products
        const uniqueCategories = [...new Set(products.map(product => product.category))].slice(0, 3);
        
        // Create category objects with sample images
        const categoryData = uniqueCategories.map((cat, index) => ({
          id: index + 1,
          img: getCategoryImage(cat),
          title: cat.toUpperCase(),
          cat: cat.toLowerCase()
        }));
        
        setCategories(categoryData);
      } catch (error) {
        console.error('Error fetching categories:', error);
        // Fallback to default categories if API fails
        setCategories(getDefaultCategories());
      } finally {
        setLoading(false);
      }
    };

    fetchCategories();
  }, []);

  const getCategoryImage = (category) => {
    const images = {
      'men': 'https://images.unsplash.com/photo-1487222477894-8943e31ef7b2?w=600',
      'women': 'https://images.unsplash.com/photo-1620799140408-edc6dcb6d633?w=600',
      'electronics': 'https://images.unsplash.com/photo-1586790170083-2f9ceadc732d?w=600',
      'default': 'https://images.unsplash.com/photo-1441986300917-64674bd600d8?w=600'
    };
    
    return images[category.toLowerCase()] || images.default;
  };

  const getDefaultCategories = () => [
    {
      id: 1,
      img: 'https://images.unsplash.com/photo-1487222477894-8943e31ef7b2?w=600',
      title: "MEN'S FASHION",
      cat: "men"
    },
    {
      id: 2,
      img: 'https://images.unsplash.com/photo-1620799140408-edc6dcb6d633?w=600',
      title: "WOMEN'S FASHION", 
      cat: "women"
    },
    {
      id: 3,
      img: 'https://images.unsplash.com/photo-1586790170083-2f9ceadc732d?w=600',
      title: "ELECTRONICS",
      cat: "electronics"
    }
  ];

  if (loading) {
    return (
      <Container>
        <div style={{ textAlign: 'center', padding: '40px' }}>
          Loading categories...
        </div>
      </Container>
    );
  }

  return (
    <Container>
      {categories.map((item) => (
        <CategoryItem item={item} key={item.id} />
      ))}
    </Container>
  );
};

export default Categories;
