import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import styled from 'styled-components';

const Container = styled.div`
  padding: 50px;
  display: flex;
`;

const ImageContainer = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Image = styled.img`
  width: 80%;
  height: 500px;
  object-fit: contain;
`;

const InfoContainer = styled.div`
  flex: 1;
  padding: 0 50px;
`;

const Title = styled.h1`
  font-weight: 200;
`;

const Desc = styled.p`
  margin: 20px 0;
`;

const Price = styled.span`
  font-weight: 100;
  font-size: 40px;
`;

const FilterContainer = styled.div`
  width: 50%;
  margin: 30px 0;
  display: flex;
  justify-content: space-between;
`;

const Filter = styled.div`
  display: flex;
  align-items: center;
`;

const FilterTitle = styled.span`
  font-size: 20px;
  font-weight: 200;
`;

const Select = styled.select`
  margin-left: 10px;
  padding: 5px;
`;

const AddContainer = styled.div`
  width: 50%;
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

const Button = styled.button`
  padding: 15px;
  border: 2px solid teal;
  background-color: white;
  cursor: pointer;
  font-weight: 500;
  
  &:hover {
    background-color: #f8f4f4;
  }
`;

const ProductPage = () => {
  const { id } = useParams();
  const [product, setProduct] = useState({});
  const [selectedSize, setSelectedSize] = useState('');
  const [selectedColor, setSelectedColor] = useState('');
  
  useEffect(() => {
    const getProduct = async () => {
      try {
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/products/${id}`);
        setProduct(res.data);
        setSelectedSize(res.data.size && res.data.size.length > 0 ? res.data.size[0] : '');
        setSelectedColor(res.data.color && res.data.color.length > 0 ? res.data.color[0] : '');
      } catch (err) {
        console.log(err);
      }
    };
    getProduct();
  }, [id]);

  return (
    <Container>
      <ImageContainer>
        <Image src={product.img} />
      </ImageContainer>
      <InfoContainer>
        <Title>{product.title}</Title>
        <Desc>{product.desc}</Desc>
        <Price>${product.price}</Price>
        <FilterContainer>
          <Filter>
            <FilterTitle>Size</FilterTitle>
            <Select onChange={(e) => setSelectedSize(e.target.value)}>
              {product.size && product.size.map((s) => (
                <option key={s}>{s}</option>
              ))}
            </Select>
          </Filter>
          <Filter>
            <FilterTitle>Color</FilterTitle>
            <Select onChange={(e) => setSelectedColor(e.target.value)}>
              {product.color && product.color.map((c) => (
                <option key={c}>{c}</option>
              ))}
            </Select>
          </Filter>
        </FilterContainer>
        <AddContainer>
          <Button>ADD TO CART</Button>
        </AddContainer>
      </InfoContainer>
    </Container>
  );
};

export default ProductPage;
