import React, { useState } from "react";
import styled from "styled-components";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Info = styled.div`
  opacity: 0;
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  left: 0;
  background-color: rgba(0, 0, 0, 0.2);
  z-index: 3;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.5s ease;
  cursor: pointer;
`;

const Container = styled.div`
  flex: 1;
  margin: 5px;
  min-width: 280px;
  height: 480px;
  display: flex;
  flex-direction: column;
  align-items: center;
  background-color: #f5fbfd;
  position: relative;
  padding: 10px;
  box-shadow: 0 2px 8px rgba(0,0,0,0.1);
  border-radius: 8px;
  cursor: pointer;
  
  &:hover ${Info} {
    opacity: 1;
  }
`;

const ProductDetails = styled.div`
  width: 100%;
  padding: 10px;
  text-align: center;
  z-index: 4;
  position: relative;
  background: white;
  border-radius: 0 0 8px 8px;
`;

const ProductTitle = styled.h3`
  font-size: 16px;
  font-weight: 600;
  margin: 8px 0;
  color: #333;
  height: 40px;
  overflow: hidden;
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
`;

const ProductPrice = styled.p`
  font-size: 18px;
  font-weight: bold;
  color: #2874f0;
  margin: 5px 0;
`;

const SelectionContainer = styled.div`
  width: 100%;
  margin: 8px 0;
`;

const Select = styled.select`
  width: 90%;
  padding: 8px;
  margin: 5px 0;
  border: 1px solid #ddd;
  border-radius: 4px;
  font-size: 14px;
`;

const AddToCartButton = styled.button`
  width: 90%;
  padding: 10px;
  background-color: #2874f0;
  color: white;
  border: none;
  border-radius: 4px;
  font-size: 14px;
  font-weight: bold;
  cursor: pointer;
  margin-top: 8px;
  
  &:hover {
    background-color: #1c60d1;
  }
`;

const ImageContainer = styled.div`
  position: relative;
  width: 100%;
  height: 250px;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: #f5fbfd;
  border-radius: 8px 8px 0 0;
`;

const Circle = styled.div`
  width: 180px;
  height: 180px;
  border-radius: 50%;
  background-color: white;
  position: absolute;
`;

const Image = styled.img`
  height: 200px;
  max-width: 90%;
  object-fit: contain;
  z-index: 2;
`;

const Icon = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background-color: white;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 10px;
  transition: all 0.5s ease;
  cursor: pointer;
  
  &:hover {
    background-color: #e9f5f5;
    transform: scale(1.1);
  }
`;

const Product = ({ item }) => {
  const navigate = useNavigate();
  const [selectedSize, setSelectedSize] = useState(item.size && item.size.length > 0 ? item.size[0] : '');
  const [selectedColor, setSelectedColor] = useState(item.color && item.color.length > 0 ? item.color[0] : '');
  
  console.log('Product data:', item);
  
  // Navigate to single product page
  const handleProductClick = () => {
    navigate(`/product/${item.id}`);
  };

  const addToCart = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login to add items to cart');
      return;
    }

    try {
      const base64Payload = token.split('.')[1];
      const payload = JSON.parse(atob(base64Payload));
      const userId = payload.userId;

      const cartItem = {
        productId: item.id,
        quantity: 1,
        price: item.price,
        title: item.title,
        img: item.img,
        size: selectedSize,
        color: selectedColor
      };

      await axios.post(
        `${process.env.REACT_APP_API_URL}/api/cart/${userId}`,
        cartItem,
        { 
          headers: { 
            Authorization: `Bearer ${token}` 
          } 
        }
      );
      
      alert(`Added to cart: ${item.title} (Size: ${selectedSize}, Color: ${selectedColor})`);
    } catch (error) {
      console.error('Error adding to cart:', error);
      alert('Failed to add product to cart');
    }
  };

  // Use product image or fallback
  const imageUrl = item.img || `https://via.placeholder.com/300x300/4A90E2/FFFFFF?text=${encodeURIComponent(item.title || 'Product')}`;
  
  return (
    <Container onClick={handleProductClick}>
      <ImageContainer>
        <Circle />
        <Image 
          src={imageUrl} 
          alt={item.title}
          onError={(e) => {
            e.target.src = `https://via.placeholder.com/300x300/4A90E2/FFFFFF?text=${encodeURIComponent(item.title || 'Product')}`;
          }}
        />
        <Info>
          <Icon onClick={(e) => {
            e.stopPropagation(); // Prevent navigating to product page
            addToCart();
          }}>
            <i className="fas fa-shopping-cart"></i>
          </Icon>
          <Icon>
            <i className="fas fa-search"></i>
          </Icon>
          <Icon>
            <i className="fas fa-heart"></i>
          </Icon>
        </Info>
      </ImageContainer>
      
      {/* PRODUCT DETAILS WITH SIZE & COLOR SELECTION */}
      <ProductDetails onClick={(e) => e.stopPropagation()}>
        <ProductTitle>{item.title}</ProductTitle>
        <ProductPrice>${item.price}</ProductPrice>
        
        {/* SIZE SELECTION */}
        {item.size && item.size.length > 0 && (
          <SelectionContainer>
            <label><strong>Size:</strong></label>
            <Select 
              value={selectedSize} 
              onChange={(e) => setSelectedSize(e.target.value)}
            >
              {item.size.map((size) => (
                <option key={size} value={size}>{size}</option>
              ))}
            </Select>
          </SelectionContainer>
        )}
        
        {/* COLOR SELECTION */}
        {item.color && item.color.length > 0 && (
          <SelectionContainer>
            <label><strong>Color:</strong></label>
            <Select 
              value={selectedColor} 
              onChange={(e) => setSelectedColor(e.target.value)}
            >
              {item.color.map((color) => (
                <option key={color} value={color}>{color}</option>
              ))}
            </Select>
          </SelectionContainer>
        )}
        
        {/* ADD TO CART BUTTON */}
        <AddToCartButton onClick={addToCart}>
          ADD TO CART
        </AddToCartButton>
      </ProductDetails>
    </Container>
  );
};

export default Product;
