import React, { useState, useEffect } from "react";
import {
  NavbarContainer,
  LeftContainer,
  RightContainer,
  NavbarExtendedContainer,
  NavbarInnerContainer,
  NavbarLinkContainer,
  NavbarLink,
  Logo,
  OpenLinksButton,
  NavbarLinkExtended,
} from "./styles/Navbar.styles";

function Navbar() {
  const [extendNavbar, setExtendNavbar] = useState(false);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      // Decode token to get user info (since we're using simple token decoding)
      try {
        const base64Payload = token.split('.')[1];
        const payload = JSON.parse(atob(base64Payload));
        setUser(payload);
      } catch (error) {
        console.error('Error decoding token:', error);
        localStorage.removeItem('token');
      }
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    window.location.href = '/';
  };

  return (
    <NavbarContainer extendNavbar={extendNavbar}>
      <NavbarInnerContainer>
        <LeftContainer>
          <NavbarLinkContainer>
            <NavbarLink to="/"> Home</NavbarLink>
            <NavbarLink to="/products/men"> Men</NavbarLink>
            <NavbarLink to="/products/women"> Women</NavbarLink>
            <NavbarLink to="/products/electronics"> Electronics</NavbarLink>
            <OpenLinksButton
              onClick={() => {
                setExtendNavbar((curr) => !curr);
              }}
            >
              {extendNavbar ? <>&#10005;</> : <>&#8801;</>}
            </OpenLinksButton>
          </NavbarLinkContainer>
        </LeftContainer>
        <RightContainer>
          <Logo>QuickCart</Logo>
        </RightContainer>
      </NavbarInnerContainer>
      {extendNavbar && (
        <NavbarExtendedContainer>
          <NavbarLinkExtended to="/"> Home</NavbarLinkExtended>
          <NavbarLinkExtended to="/products/men"> Men</NavbarLinkExtended>
          <NavbarLinkExtended to="/products/women"> Women</NavbarLinkExtended>
          <NavbarLinkExtended to="/products/electronics"> Electronics</NavbarLinkExtended>
          {user ? (
            <>
              <NavbarLinkExtended to="/profile">Profile</NavbarLinkExtended>
              <NavbarLinkExtended onClick={handleLogout} to="/">
                Logout ({user.email})
              </NavbarLinkExtended>
            </>
          ) : (
            <>
              <NavbarLinkExtended to="/login">Login</NavbarLinkExtended>
              <NavbarLinkExtended to="/register">Register</NavbarLinkExtended>
            </>
          )}
        </NavbarExtendedContainer>
      )}
    </NavbarContainer>
  );
}

export default Navbar;
