import axios from 'axios';

const API_BASE = process.env.REACT_APP_API_URL;

export const productService = {
  getAllProducts: async () => {
    const response = await axios.get(`${API_BASE}/api/products`);
    return response.data;
  },

  getProductById: async (id) => {
    const response = await axios.get(`${API_BASE}/api/products/${id}`);
    return response.data;
  },

  getProductsByCategory: async (category) => {
    const response = await axios.get(`${API_BASE}/api/products/category/${category}`);
    return response.data;
  },

  getCategories: async () => {
    const response = await axios.get(`${API_BASE}/api/products/categories`);
    return response.data;
  },

  searchProducts: async (query) => {
    const response = await axios.get(`${API_BASE}/api/products/search?q=${query}`);
    return response.data;
  }
};
