import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import { useCart } from "./CartContext";
import axios from "axios";

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
  background: white;
  border-radius: 15px;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease;
  height: 100%;
  position: relative;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 30px rgba(0, 0, 0, 0.15);
  }

  @media (max-width: 768px) {
    padding: 15px;
    margin: 10px;
  }

  @media (max-width: 480px) {
    padding: 12px;
    margin: 8px;
  }
`;

const ImageContainer = styled.div`
  position: relative;
  width: 100%;
  height: 250px;
  overflow: hidden;
  border-radius: 10px;
  margin-bottom: 15px;
  cursor: pointer;

  @media (max-width: 768px) {
    height: 200px;
  }

  @media (max-width: 480px) {
    height: 180px;
  }
`;

const Image = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.3s ease;

  ${Container}:hover & {
    transform: scale(1.05);
  }
`;

const InfoContainer = styled.div`
  width: 100%;
  padding: 0 10px;
`;

const Title = styled.h3`
  font-size: 1.2rem;
  font-weight: 600;
  color: #2d3748;
  margin-bottom: 10px;
  line-height: 1.3;
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  overflow: hidden;
  min-height: 2.6em;

  @media (max-width: 768px) {
    font-size: 1.1rem;
  }

  @media (max-width: 480px) {
    font-size: 1rem;
    min-height: 2.4em;
  }
`;

const Description = styled.p`
  color: #718096;
  font-size: 0.9rem;
  line-height: 1.4;
  margin-bottom: 12px;
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  overflow: hidden;
  min-height: 2.8em;

  @media (max-width: 480px) {
    font-size: 0.85rem;
    min-height: 2.6em;
  }
`;

const PriceContainer = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;
  margin-bottom: 12px;
  flex-wrap: wrap;
`;

const Price = styled.span`
  font-size: 1.3rem;
  font-weight: 700;
  color: #2d3748;

  @media (max-width: 480px) {
    font-size: 1.2rem;
  }
`;

const OriginalPrice = styled.span`
  font-size: 1rem;
  color: #a0aec0;
  text-decoration: line-through;

  @media (max-width: 480px) {
    font-size: 0.9rem;
  }
`;

const DiscountBadge = styled.span`
  background: linear-gradient(135deg, #ff6b6b, #ee5a52);
  color: white;
  padding: 4px 8px;
  border-radius: 12px;
  font-size: 0.8rem;
  font-weight: 600;

  @media (max-width: 480px) {
    font-size: 0.75rem;
    padding: 3px 6px;
  }
`;

const ColorOptions = styled.div`
  display: flex;
  gap: 6px;
  margin-bottom: 12px;
  flex-wrap: wrap;
`;

const ColorOption = styled.div`
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background-color: ${(props) => props.color};
  border: 2px solid ${(props) => (props.selected ? "#667eea" : "transparent")};
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    transform: scale(1.1);
  }

  @media (max-width: 480px) {
    width: 18px;
    height: 18px;
  }
`;

const SizeOptions = styled.div`
  display: flex;
  gap: 6px;
  margin-bottom: 15px;
  flex-wrap: wrap;
`;

const SizeOption = styled.button`
  padding: 6px 12px;
  border: 1px solid ${(props) => (props.selected ? "#667eea" : "#e2e8f0")};
  background: ${(props) => (props.selected ? "#667eea" : "white")};
  color: ${(props) => (props.selected ? "white" : "#4a5568")};
  border-radius: 6px;
  font-size: 0.8rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    border-color: #667eea;
    background: ${(props) => (props.selected ? "#667eea" : "#f7fafc")};
  }

  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }

  @media (max-width: 480px) {
    padding: 5px 10px;
    font-size: 0.75rem;
  }
`;

const Button = styled.button`
  width: 100%;
  padding: 12px 20px;
  background: ${(props) => 
    props.disabled ? "#cbd5e0" : "linear-gradient(135deg, #667eea 0%, #764ba2 100%)"};
  color: white;
  border: none;
  border-radius: 8px;
  font-size: 0.95rem;
  font-weight: 600;
  cursor: ${(props) => (props.disabled ? "not-allowed" : "pointer")};
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;

  &:hover {
    transform: ${(props) => (props.disabled ? "none" : "translateY(-2px)")};
    box-shadow: ${(props) => 
      props.disabled ? "none" : "0 4px 15px rgba(102, 126, 234, 0.4)"};
  }

  @media (max-width: 480px) {
    padding: 14px 20px;
    font-size: 0.9rem;
  }
`;

const StockInfo = styled.div`
  display: flex;
  align-items: center;
  gap: 6px;
  margin-bottom: 10px;
  font-size: 0.85rem;
  color: ${(props) => (props.inStock ? "#38a169" : "#e53e3e")};
  font-weight: 500;

  @media (max-width: 480px) {
    font-size: 0.8rem;
  }
`;

const CategoryTag = styled.span`
  background: #edf2f7;
  color: #4a5568;
  padding: 4px 8px;
  border-radius: 6px;
  font-size: 0.75rem;
  font-weight: 500;
  margin-bottom: 8px;
  display: inline-block;

  @media (max-width: 480px) {
    font-size: 0.7rem;
    padding: 3px 6px;
  }
`;

const QuickViewOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.7);
  display: flex;
  align-items: center;
  justify-content: center;
  opacity: 0;
  transition: opacity 0.3s ease;
  border-radius: 10px;

  ${Container}:hover & {
    opacity: 1;
  }

  @media (max-width: 768px) {
    opacity: 1;
    background: rgba(0, 0, 0, 0.5);
  }
`;

const QuickViewButton = styled.button`
  background: white;
  color: #2d3748;
  border: none;
  padding: 10px 20px;
  border-radius: 25px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: #667eea;
    color: white;
    transform: translateY(-2px);
  }

  @media (max-width: 480px) {
    padding: 8px 16px;
    font-size: 0.9rem;
  }
`;

const LoadingSkeleton = styled.div`
  width: 100%;
  height: 250px;
  background: linear-gradient(90deg, #f0f0f0 25%, #e0e0e0 50%, #f0f0f0 75%);
  background-size: 200% 100%;
  animation: loading 1.5s infinite;
  border-radius: 10px;
  margin-bottom: 15px;

  @keyframes loading {
    0% {
      background-position: 200% 0;
    }
    100% {
      background-position: -200% 0;
    }
  }

  @media (max-width: 768px) {
    height: 200px;
  }

  @media (max-width: 480px) {
    height: 180px;
  }
`;

const Product = ({ product, loading }) => {
  const navigate = useNavigate();
  const { addToCart: addToCartContext } = useCart();
  const [selectedColor, setSelectedColor] = useState("");
  const [selectedSize, setSelectedSize] = useState("");
  const [currentImage, setCurrentImage] = useState("");
  const [currentSizes, setCurrentSizes] = useState([]);
  const [isAddingToCart, setIsAddingToCart] = useState(false);

  // Initialize product data
  useEffect(() => {
    if (product) {
      // Set default color
      const defaultColor = product.color && product.color.length > 0 ? product.color[0] : "";
      setSelectedColor(defaultColor);
      
      // Set default image
      const displayImage = product.colorImages && product.colorImages[defaultColor] 
        ? product.colorImages[defaultColor] 
        : product.img;
      setCurrentImage(displayImage);
      
      // Set available sizes
      const sizes = getAvailableSizes(product, defaultColor);
      setCurrentSizes(sizes);
      
      // Set default size
      if (sizes.length > 0) {
        setSelectedSize(sizes[0]);
      }
    }
  }, [product]);

  // Get available sizes for selected color
  const getAvailableSizes = (productData, color) => {
    if (!productData.inventory || !productData.size) {
      return productData.size || [];
    }

    // Filter sizes that have quantity > 0 for the selected color
    return productData.size.filter(size => {
      const inventoryItem = productData.inventory[color]?.[size];
      return inventoryItem && inventoryItem.quantity > 0;
    });
  };

  // Get current price based on color and size
  const getCurrentPrice = () => {
    if (!selectedColor || !selectedSize || !product.inventory) {
      return product.price || 0;
    }

    const inventoryItem = product.inventory[selectedColor]?.[selectedSize];
    return inventoryItem?.price || product.price || 0;
  };

  // Handle color selection
  const handleColorSelect = (color) => {
    setSelectedColor(color);
    
    // Update image
    if (product.colorImages && product.colorImages[color]) {
      setCurrentImage(product.colorImages[color]);
    }
    
    // Update available sizes
    const sizes = getAvailableSizes(product, color);
    setCurrentSizes(sizes);
    
    // Reset or set size
    if (sizes.length > 0 && !sizes.includes(selectedSize)) {
      setSelectedSize(sizes[0]);
    } else if (sizes.length === 0) {
      setSelectedSize("");
    }
  };

  // Handle size selection
  const handleSizeSelect = (size) => {
    setSelectedSize(size);
  };

  // Handle product click for quick view
  const handleProductClick = () => {
    navigate(`/product/${product.id}`);
  };

  // Handle add to cart
  const addToCart = async () => {
    if (!selectedColor || (product.size && product.size.length > 0 && !selectedSize)) {
      alert("Please select color and size options");
      return;
    }

    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login to add items to cart');
      navigate('/login');
      return;
    }

    setIsAddingToCart(true);

    try {
      const base64Payload = token.split('.')[1];
      const payload = JSON.parse(atob(base64Payload));
      const userId = payload.userId;

      const cartItem = {
        productId: product.id,
        quantity: 1,
        price: getCurrentPrice(),
        title: product.title,
        img: currentImage,
        size: selectedSize || 'One Size',
        color: selectedColor || 'Default'
      };

      await axios.post(
        `${process.env.REACT_APP_API_URL}/api/cart/${userId}`,
        cartItem,
        { 
          headers: { 
            Authorization: `Bearer ${token}` 
          } 
        }
      );

      // Update local cart context
      addToCartContext(cartItem);

      // Show success feedback
      alert('Product added to cart successfully!');
      
    } catch (error) {
      console.error('Error adding to cart:', error);
      alert('Failed to add product to cart');
    } finally {
      setIsAddingToCart(false);
    }
  };

  // Check if product is in stock
  const isInStock = () => {
    if (!product.inventory) return true; // Assume in stock if no inventory data
    
    if (selectedColor && selectedSize) {
      const inventoryItem = product.inventory[selectedColor]?.[selectedSize];
      return inventoryItem && inventoryItem.quantity > 0;
    }
    
    // Check if any combination is in stock
    if (product.color && product.color.length > 0) {
      return product.color.some(color => {
        const sizes = getAvailableSizes(product, color);
        return sizes.length > 0;
      });
    }
    
    return true;
  };

  // Calculate discount percentage
  const getDiscountPercentage = () => {
    const currentPrice = getCurrentPrice();
    const originalPrice = product.price;
    
    if (currentPrice < originalPrice) {
      return Math.round(((originalPrice - currentPrice) / originalPrice) * 100);
    }
    return 0;
  };

  if (loading) {
    return (
      <Container>
        <LoadingSkeleton />
        <InfoContainer>
          <div style={{ height: '20px', background: '#f0f0f0', borderRadius: '4px', marginBottom: '10px' }}></div>
          <div style={{ height: '16px', background: '#f0f0f0', borderRadius: '4px', marginBottom: '10px', width: '80%' }}></div>
          <div style={{ height: '20px', background: '#f0f0f0', borderRadius: '4px', marginBottom: '15px', width: '60%' }}></div>
          <div style={{ height: '40px', background: '#f0f0f0', borderRadius: '8px' }}></div>
        </InfoContainer>
      </Container>
    );
  }

  if (!product) {
    return null;
  }

  const discountPercentage = getDiscountPercentage();
  const inStock = isInStock();

  return (
    <Container>
      <ImageContainer onClick={handleProductClick}>
        <Image 
          src={currentImage || product.img} 
          alt={product.title}
          onError={(e) => {
            e.target.src = `https://via.placeholder.com/300x300/667eea/FFFFFF?text=${encodeURIComponent(product.title)}`;
          }}
        />
        <QuickViewOverlay>
          <QuickViewButton onClick={handleProductClick}>
            Quick View
          </QuickViewButton>
        </QuickViewOverlay>
      </ImageContainer>

      <InfoContainer>
        {product.categories && product.categories.length > 0 && (
          <CategoryTag>{product.categories[0]}</CategoryTag>
        )}
        
        <Title>{product.title}</Title>
        
        <Description>{product.description || product.desc || "No description available"}</Description>

        <PriceContainer>
          <Price>${getCurrentPrice().toFixed(2)}</Price>
          {discountPercentage > 0 && (
            <>
              <OriginalPrice>${product.price.toFixed(2)}</OriginalPrice>
              <DiscountBadge>-{discountPercentage}%</DiscountBadge>
            </>
          )}
        </PriceContainer>

        <StockInfo inStock={inStock}>
          <i className={`fas ${inStock ? 'fa-check-circle' : 'fa-times-circle'}`}></i>
          {inStock ? 'In Stock' : 'Out of Stock'}
        </StockInfo>

        {product.color && product.color.length > 0 && (
          <ColorOptions>
            {product.color.map((color) => (
              <ColorOption
                key={color}
                color={color}
                selected={selectedColor === color}
                onClick={() => handleColorSelect(color)}
                title={color}
              />
            ))}
          </ColorOptions>
        )}

        {currentSizes && currentSizes.length > 0 && (
          <SizeOptions>
            {currentSizes.map((size) => (
              <SizeOption
                key={size}
                selected={selectedSize === size}
                onClick={() => handleSizeSelect(size)}
              >
                {size}
              </SizeOption>
            ))}
          </SizeOptions>
        )}

        <Button 
          onClick={addToCart}
          disabled={!inStock || isAddingToCart || (product.size && product.size.length > 0 && !selectedSize) || (product.color && product.color.length > 0 && !selectedColor)}
        >
          {isAddingToCart ? (
            <>
              <i className="fas fa-spinner fa-spin"></i>
              Adding...
            </>
          ) : !inStock ? (
            <>
              <i className="fas fa-times"></i>
              Out of Stock
            </>
          ) : (
            <>
              <i className="fas fa-shopping-cart"></i>
              Add to Cart
            </>
          )}
        </Button>
      </InfoContainer>
    </Container>
  );
};

export default Product;
