import React, { useState, useEffect, useCallback } from "react";
import styled from "styled-components";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { loadStripe } from "@stripe/stripe-js";
import { Elements } from "@stripe/react-stripe-js";
import StripePayment from "../components/payment/StripePayment";

// Initialize Stripe with your publishable key
const stripePromise = loadStripe("pk_test_51SEWfbGUJ7v3D9GXVs5sPIBEY08wZrpGmeuko8YClRovgbeQkkYJ2NpxLMtM3RyA8v7NSKM4cE8mTLthWafiYGv500lPgVJb3n");

// ... (keep all your existing styled components exactly as they are)

const CartPage = () => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [isCheckingOut, setIsCheckingOut] = useState(false);
  const [showAddressForm, setShowAddressForm] = useState(false);
  const [showPayment, setShowPayment] = useState(false);
  const [paymentError, setPaymentError] = useState('');
  const [clientSecret, setClientSecret] = useState('');
  const [currentOrderId, setCurrentOrderId] = useState('');
  const navigate = useNavigate();

  const [address, setAddress] = useState({
    street: "",
    city: "",
    state: "",
    zipCode: "",
    country: ""
  });

  // ... (keep all your existing helper functions exactly as they are)

  const handlePayment = async () => {
    setPaymentError('');
    setIsCheckingOut(true);

    try {
      const userId = getCurrentUser();
      const token = localStorage.getItem('token');
      
      const BASE_URL = process.env.REACT_APP_API_URL;
      console.log('Using base URL:', BASE_URL);

      // 1. CREATE THE ORDER
      const orderData = {
        cartItems: cartItems.map(item => ({
          productId: item.productId,
          title: item.title,
          price: item.price,
          quantity: item.quantity,
          size: item.size || 'One Size',
          color: item.color || 'Default',
          img: item.img
        })),
        address: address,
        totalAmount: getFinalTotal()
      };

      console.log('Creating order with data:', orderData);

      // Create order first
      const orderResponse = await axios.post(
        `${BASE_URL}/api/orders`,
        orderData,
        { 
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      console.log('Order created:', orderResponse.data);
      const createdOrderId = orderResponse.data.order.id;
      setCurrentOrderId(createdOrderId);

      const amount = Math.round(getFinalTotal() * 100);

      console.log('Creating payment intent for amount:', amount, 'Order ID:', createdOrderId);

      // 2. CREATE PAYMENT INTENT (This returns clientSecret for Stripe)
      const paymentResponse = await axios.post(
        `${BASE_URL}/api/payments/create-payment-intent`,
        { 
          orderId: createdOrderId,
          amount 
        },
        { 
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          },
          timeout: 10000
        }
      );

      console.log('Payment intent created:', paymentResponse.data);

      // Set client secret for Stripe Elements
      if (paymentResponse.data.clientSecret) {
        setClientSecret(paymentResponse.data.clientSecret);
      } else {
        throw new Error('No client secret received from payment service');
      }

    } catch (error) {
      console.error('Payment setup error:', error);
      setPaymentError(`Payment setup failed: ${error.response?.data?.error || error.message}`);
      setIsCheckingOut(false);
    }
  };

  // Handle successful Stripe payment
  const handlePaymentSuccess = async (paymentIntent) => {
    try {
      const userId = getCurrentUser();
      const token = localStorage.getItem('token');
      const BASE_URL = process.env.REACT_APP_API_URL;

      console.log('Stripe payment succeeded:', paymentIntent);

      // Clear cart after successful payment
      try {
        await axios.delete(
          `${BASE_URL}/api/cart/${userId}/clear`,
          { 
            headers: { 
              Authorization: `Bearer ${token}` 
            } 
          }
        );
        console.log('Cart cleared successfully');
      } catch (cartError) {
        console.log('Cart clearing failed, but continuing...', cartError);
      }

      // Clear cart locally
      setCartItems([]);

      // Show success and redirect
      alert(`Payment successful! Order ID: ${currentOrderId}`);
      setShowPayment(false);
      setClientSecret('');
      navigate('/success');

    } catch (error) {
      console.error('Post-payment processing error:', error);
    }
  };

  const handlePaymentError = (errorMessage) => {
    setPaymentError(`Payment failed: ${errorMessage}`);
  };

  // ... (keep all your existing JSX exactly as it is, but update the PaymentModal)

  return (
    <Container>
      <Wrapper>
        {/* ... (keep all your existing JSX exactly as it is) */}
      </Wrapper>

      {/* Updated Payment Modal with Stripe */}
      {showPayment && (
        <PaymentModal>
          <PaymentContent>
            <h2>Complete Your Purchase</h2>
            <p>Total Amount: ${getFinalTotal().toFixed(2)}</p>
            
            {paymentError && <ErrorMessage>{paymentError}</ErrorMessage>}
            
            {clientSecret ? (
              <Elements stripe={stripePromise} options={{ clientSecret }}>
                <StripePayment 
                  clientSecret={clientSecret}
                  amount={getFinalTotal().toFixed(2)}
                  onSuccess={handlePaymentSuccess}
                  onError={handlePaymentError}
                />
              </Elements>
            ) : (
              <div>
                <PayNowBtn 
                  onClick={handlePayment} 
                  disabled={isCheckingOut}
                >
                  {isCheckingOut ? 'Setting Up Payment...' : 'Proceed to Payment'}
                </PayNowBtn>
              </div>
            )}
            
            <CancelBtn onClick={() => {
              setShowPayment(false);
              setClientSecret('');
              setPaymentError('');
            }}>
              Cancel
            </CancelBtn>
          </PaymentContent>
        </PaymentModal>
      )}
    </Container>
  );
};

export default CartPage;
