import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import styled from 'styled-components';

const Container = styled.div`
  padding: 50px;
  display: flex;
  flex-wrap: wrap;
`;

const ImageContainer = styled.div`
  flex: 1;
  min-width: 300px;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 20px;
`;

const Image = styled.img`
  width: 100%;
  max-width: 500px;
  height: 500px;
  object-fit: contain;
`;

const InfoContainer = styled.div`
  flex: 1;
  min-width: 300px;
  padding: 0 50px;
`;

const Title = styled.h1`
  font-weight: 300;
  font-size: 2.5rem;
  margin-bottom: 20px;
`;

const Desc = styled.p`
  margin: 20px 0;
  font-size: 1.1rem;
  line-height: 1.6;
  color: #555;
`;

const Price = styled.span`
  font-weight: 300;
  font-size: 2rem;
  color: #2874f0;
  display: block;
  margin: 20px 0;
`;

const FilterContainer = styled.div`
  width: 100%;
  margin: 30px 0;
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const Filter = styled.div`
  display: flex;
  align-items: center;
  gap: 15px;
`;

const FilterTitle = styled.span`
  font-size: 1.2rem;
  font-weight: 300;
  min-width: 80px;
`;

const Select = styled.select`
  padding: 10px 15px;
  border: 1px solid #ddd;
  border-radius: 4px;
  font-size: 1rem;
  min-width: 150px;
`;

const AddContainer = styled.div`
  width: 100%;
  display: flex;
  gap: 20px;
  margin-top: 40px;
`;

const Button = styled.button`
  padding: 15px 30px;
  border: 2px solid #2874f0;
  background-color: ${props => props.primary ? '#2874f0' : 'white'};
  color: ${props => props.primary ? 'white' : '#2874f0'};
  cursor: pointer;
  font-weight: 500;
  font-size: 1rem;
  border-radius: 4px;
  transition: all 0.3s ease;
  
  &:hover {
    background-color: ${props => props.primary ? '#1c60d1' : '#f8f4f4'};
  }
`;

const BackButton = styled.button`
  padding: 10px 20px;
  background-color: #666;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  margin-bottom: 20px;
  font-size: 1rem;
  
  &:hover {
    background-color: #555;
  }
`;

const ProductPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState({});
  const [selectedSize, setSelectedSize] = useState('');
  const [selectedColor, setSelectedColor] = useState('');
  const [loading, setLoading] = useState(true);
  
  useEffect(() => {
    const getProduct = async () => {
      try {
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/products/${id}`);
        setProduct(res.data);
        setSelectedSize(res.data.size && res.data.size.length > 0 ? res.data.size[0] : '');
        setSelectedColor(res.data.color && res.data.color.length > 0 ? res.data.color[0] : '');
        setLoading(false);
      } catch (err) {
        console.log(err);
        setLoading(false);
      }
    };
    getProduct();
  }, [id]);

  const addToCart = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login to add items to cart');
      return;
    }

    try {
      const base64Payload = token.split('.')[1];
      const payload = JSON.parse(atob(base64Payload));
      const userId = payload.userId;

      const cartItem = {
        productId: product.id,
        quantity: 1,
        price: product.price,
        title: product.title,
        img: product.img,
        size: selectedSize,
        color: selectedColor
      };

      await axios.post(
        `${process.env.REACT_APP_API_URL}/api/cart/${userId}`,
        cartItem,
        { 
          headers: { 
            Authorization: `Bearer ${token}` 
          } 
        }
      );
      
      alert(`Added to cart: ${product.title} (Size: ${selectedSize}, Color: ${selectedColor})`);
    } catch (error) {
      console.error('Error adding to cart:', error);
      alert('Failed to add product to cart');
    }
  };

  if (loading) {
    return (
      <Container>
        <div style={{ textAlign: 'center', width: '100%', padding: '50px' }}>
          Loading product...
        </div>
      </Container>
    );
  }

  return (
    <div style={{ padding: '20px' }}>
      <BackButton onClick={() => navigate(-1)}>
        ← Back to Products
      </BackButton>
      
      <Container>
        <ImageContainer>
          <Image 
            src={product.img} 
            alt={product.title}
            onError={(e) => {
              e.target.src = `https://via.placeholder.com/500x500/4A90E2/FFFFFF?text=${encodeURIComponent(product.title || 'Product')}`;
            }}
          />
        </ImageContainer>
        <InfoContainer>
          <Title>{product.title}</Title>
          <Desc>{product.desc}</Desc>
          <Price>${product.price}</Price>
          
          <FilterContainer>
            {product.size && product.size.length > 0 && (
              <Filter>
                <FilterTitle>Size:</FilterTitle>
                <Select 
                  value={selectedSize} 
                  onChange={(e) => setSelectedSize(e.target.value)}
                >
                  {product.size.map((size) => (
                    <option key={size} value={size}>{size}</option>
                  ))}
                </Select>
              </Filter>
            )}
            
            {product.color && product.color.length > 0 && (
              <Filter>
                <FilterTitle>Color:</FilterTitle>
                <Select 
                  value={selectedColor} 
                  onChange={(e) => setSelectedColor(e.target.value)}
                >
                  {product.color.map((color) => (
                    <option key={color} value={color}>{color}</option>
                  ))}
                </Select>
              </Filter>
            )}
          </FilterContainer>
          
          <AddContainer>
            <Button primary onClick={addToCart}>
              ADD TO CART
            </Button>
            <Button>
              BUY NOW
            </Button>
          </AddContainer>
        </InfoContainer>
      </Container>
    </div>
  );
};

export default ProductPage;
