import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import styled from 'styled-components';
import axios from 'axios';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 60vh;
  text-align: center;
  padding: 20px;
`;

const Title = styled.h1`
  color: green;
  margin-bottom: 20px;
`;

const Message = styled.p`
  font-size: 18px;
  margin-bottom: 30px;
`;

const OrderInfo = styled.div`
  background: #f8f9fa;
  padding: 20px;
  border-radius: 10px;
  margin: 20px 0;
  text-align: left;
  max-width: 500px;
  width: 100%;
`;

const OrderDetail = styled.div`
  margin: 8px 0;
  font-size: 14px;
`;

const EmailStatus = styled.div`
  margin: 15px 0;
  padding: 10px 20px;
  border-radius: 8px;
  font-size: 14px;
  
  ${props => props.type === 'loading' && `
    background-color: #e7f3ff;
    color: #0066cc;
    border: 1px solid #b3d9ff;
  `}
  
  ${props => props.type === 'success' && `
    background-color: #d4edda;
    color: #155724;
    border: 1px solid #c3e6cb;
  `}
  
  ${props => props.type === 'error' && `
    background-color: #f8d7da;
    color: #721c24;
    border: 1px solid #f5c6cb;
  `}
`;

const Button = styled.button`
  padding: 15px 30px;
  background-color: black;
  color: white;
  border: none;
  cursor: pointer;
  font-size: 16px;
  margin: 0 10px;
  
  &:hover {
    background-color: #333;
  }
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 15px;
  flex-wrap: wrap;
  justify-content: center;
  
  @media (max-width: 480px) {
    flex-direction: column;
    align-items: center;
  }
`;

const Success = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [emailStatus, setEmailStatus] = useState('sending');
  const [orderData, setOrderData] = useState(null);

  useEffect(() => {
    const processSuccess = async () => {
      try {
        // Get order data from localStorage
        const savedOrderData = JSON.parse(localStorage.getItem('lastOrder'));
        const user = JSON.parse(localStorage.getItem('user'));
        
        console.log('🔄 Processing successful order...');
        console.log('📦 Saved order data:', savedOrderData);

        if (savedOrderData) {
          setOrderData(savedOrderData);

          // Try to send confirmation emails (optional - don't block success page)
          if (user && user.email) {
            try {
              console.log('📧 Attempting to send confirmation emails...');
              
              // Send order confirmation email
              await axios.post(
                `${process.env.REACT_APP_API_URL}/api/email/order-confirmation`,
                {
                  orderData: {
                    ...savedOrderData,
                    id: savedOrderData.id || `ORD${Date.now()}`,
                    createdAt: new Date().toISOString(),
                    status: 'confirmed'
                  },
                  userEmail: user.email
                },
                { timeout: 5000 }
              );

              setEmailStatus('sent');
              console.log('✅ Confirmation emails sent successfully');
            } catch (emailError) {
              console.error('❌ Email sending failed (non-critical):', emailError);
              setEmailStatus('failed');
              // Don't fail the whole process if emails fail
            }
          } else {
            setEmailStatus('no-data');
          }

          // Clear the order from localStorage after processing
          setTimeout(() => {
            localStorage.removeItem('lastOrder');
          }, 5000); // Keep for 5 seconds in case user refreshes
        } else {
          setEmailStatus('no-data');
          console.log('ℹ️ No order data found in localStorage');
        }
      } catch (error) {
        console.error('❌ Error in success page processing:', error);
        setEmailStatus('failed');
      }
    };

    processSuccess();
  }, []);

  const getEmailStatusMessage = () => {
    switch (emailStatus) {
      case 'sending':
        return '📧 Sending order confirmation...';
      case 'sent':
        return '✅ Order and payment confirmation emails sent!';
      case 'failed':
        return '⚠️ Order successful, but email confirmation failed.';
      case 'no-data':
        return '✅ Order successful!';
      default:
        return '';
    }
  };

  const getEmailStatusType = () => {
    switch (emailStatus) {
      case 'sending':
        return 'loading';
      case 'sent':
        return 'success';
      case 'failed':
      case 'no-data':
        return 'error';
      default:
        return 'loading';
    }
  };

  return (
    <Container>
      <Title>Order Successful! 🎉</Title>
      <Message>Thank you for your purchase. Your order has been placed successfully.</Message>
      
      {orderData && (
        <OrderInfo>
          <h3>Order Details</h3>
          <OrderDetail><strong>Order ID:</strong> {orderData.id ? `#${orderData.id.slice(-8)}` : 'Processing...'}</OrderDetail>
          <OrderDetail><strong>Amount:</strong> ${orderData.amount || orderData.total}</OrderDetail>
          <OrderDetail><strong>Status:</strong> {orderData.status || 'Confirmed'}</OrderDetail>
          <OrderDetail><strong>Payment:</strong> {orderData.paymentStatus || 'Paid'}</OrderDetail>
          <OrderDetail><strong>Items:</strong> {orderData.products ? orderData.products.length : 0} product(s)</OrderDetail>
        </OrderInfo>
      )}
      
      {emailStatus !== 'no-data' && (
        <EmailStatus type={getEmailStatusType()}>
          {getEmailStatusMessage()}
        </EmailStatus>
      )}
      
      <ButtonContainer>
        <Button onClick={() => navigate('/')}>Continue Shopping</Button>
        <Button onClick={() => navigate('/orders')}>View My Orders</Button>
      </ButtonContainer>

      <div style={{ marginTop: '20px', fontSize: '12px', color: '#666' }}>
        <p>If you see any issues, please check your orders page for confirmation.</p>
      </div>
    </Container>
  );
};

export default Success;
