import React, { useState } from 'react';
import {
  Container,
  Arrow,
  Wrapper,
  Slide,
  ImgContainer,
  Image,
  InfoContainer,
  Title,
  Desc,
  Button
} from './styles/Slider.styles';
import { ArrowLeftOutlined, ArrowRightOutlined } from '@mui/icons-material';

const Slider = () => {
  const [slideIndex, setSlideIndex] = useState(0);
  
  // Static slider data - you can make this dynamic if needed
  const sliderItems = [
    {
      id: 1,
      img: "https://images.unsplash.com/photo-1606107557195-0e29a4b5b4aa?w=800",
      title: "SUMMER SALE",
      desc: "DON'T COMPROMISE ON STYLE! GET FLAT 30% OFF FOR NEW ARRIVALS.",
      bg: "f5fafd",
    },
    {
      id: 2,
      img: "https://images.unsplash.com/photo-1542291026-7eec264c27ff?w=800",
      title: "AUTUMN COLLECTION",
      desc: "DISCOVER THE LATEST TRENDS IN FASHION WITH OUR NEW COLLECTION.",
      bg: "fcf1ed",
    },
    {
      id: 3,
      img: "https://images.unsplash.com/photo-1588099768531-a72d4a198538?w=800",
      title: "LOUNGEWEAR LOVE",
      desc: "STAY COMFORTABLE AND STYLISH WITH OUR PREMIUM LOUNGEWEAR.",
      bg: "fbf0f4",
    },
  ];

  const handleClick = (direction) => {
    if (direction === "left") {
      setSlideIndex(slideIndex > 0 ? slideIndex - 1 : sliderItems.length - 1);
    } else {
      setSlideIndex(slideIndex < sliderItems.length - 1 ? slideIndex + 1 : 0);
    }
  };

  return (
    <Container>
      <Arrow direction="left" onClick={() => handleClick("left")}>
        <ArrowLeftOutlined />
      </Arrow>
      <Wrapper slideIndex={slideIndex}>
        {sliderItems.map((item) => (
          <Slide key={item.id} bg={item.bg}>
            <ImgContainer>
              <Image src={item.img} alt={item.title} />
            </ImgContainer>
            <InfoContainer>
              <Title>{item.title}</Title>
              <Desc>{item.desc}</Desc>
              <Button>SHOP NOW</Button>
            </InfoContainer>
          </Slide>
        ))}
      </Wrapper>
      <Arrow direction="right" onClick={() => handleClick("right")}>
        <ArrowRightOutlined />
      </Arrow>
    </Container>
  );
};

export default Slider;
