import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import {
  Container,
  Wrapper,
  ImgContainer,
  Image,
  InfoContainer,
  Title,
  Desc,
  Price,
  FilterContainer,
  Filter,
  FilterTitle,
  FilterColor,
  FilterSize,
  FilterSizeOption,
  AddContainer,
  AmountContainer,
  AmountButton,
  Amount,
  Button
} from './styles/ProductPage.styles';
import { Add, Remove } from '@mui/icons-material';
import { useCart } from '../context/CartContext';
import { productService } from '../services/productService';

const ProductPage = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [color, setColor] = useState('black');
  const [size, setSize] = useState('m');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { addToCart } = useCart();

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        setLoading(true);
        const data = await productService.getProductById(id);
        setProduct(data);
        setError('');
      } catch (error) {
        console.error('Error fetching product:', error);
        setError('Product not found. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    fetchProduct();
  }, [id]);

  const handleQuantity = (type) => {
    if (type === 'dec') {
      quantity > 1 && setQuantity(quantity - 1);
    } else {
      setQuantity(quantity + 1);
    }
  };

  const handleAddToCart = () => {
    if (product) {
      addToCart({ ...product, color, size }, quantity);
      alert('Product added to cart!');
    }
  };

  if (loading) {
    return (
      <Container>
        <div style={{ textAlign: 'center', padding: '60px 20px' }}>
          Loading product...
        </div>
      </Container>
    );
  }

  if (error || !product) {
    return (
      <Container>
        <div style={{ textAlign: 'center', padding: '60px 20px', color: '#d32f2f' }}>
          {error || 'Product not found'}
        </div>
      </Container>
    );
  }

  return (
    <Container>
      <Wrapper>
        <ImgContainer>
          <Image src={product.img} alt={product.name} />
        </ImgContainer>
        <InfoContainer>
          <Title>{product.name}</Title>
          <Desc>
            {product.description || 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec venenatis, dolor in finibus malesuada.'}
          </Desc>
          <Price>$ {product.price}</Price>
          <FilterContainer>
            <Filter>
              <FilterTitle>Color</FilterTitle>
              <FilterColor 
                color="black" 
                onClick={() => setColor('black')}
                style={{ borderColor: color === 'black' ? '#333' : 'transparent' }}
              />
              <FilterColor 
                color="darkblue" 
                onClick={() => setColor('darkblue')}
                style={{ borderColor: color === 'darkblue' ? '#333' : 'transparent' }}
              />
              <FilterColor 
                color="gray" 
                onClick={() => setColor('gray')}
                style={{ borderColor: color === 'gray' ? '#333' : 'transparent' }}
              />
            </Filter>
            <Filter>
              <FilterTitle>Size</FilterTitle>
              <FilterSize value={size} onChange={(e) => setSize(e.target.value)}>
                <FilterSizeOption value="xs">XS</FilterSizeOption>
                <FilterSizeOption value="s">S</FilterSizeOption>
                <FilterSizeOption value="m">M</FilterSizeOption>
                <FilterSizeOption value="l">L</FilterSizeOption>
                <FilterSizeOption value="xl">XL</FilterSizeOption>
              </FilterSize>
            </Filter>
          </FilterContainer>
          <AddContainer>
            <AmountContainer>
              <AmountButton onClick={() => handleQuantity('dec')} disabled={quantity <= 1}>
                <Remove />
              </AmountButton>
              <Amount>{quantity}</Amount>
              <AmountButton onClick={() => handleQuantity('inc')}>
                <Add />
              </AmountButton>
            </AmountContainer>
            <Button onClick={handleAddToCart}>ADD TO CART</Button>
          </AddContainer>
        </InfoContainer>
      </Wrapper>
    </Container>
  );
};

export default ProductPage;
