import React, { useState, useEffect, useCallback } from "react";
import styled from "styled-components";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { loadStripe } from "@stripe/stripe-js";
import { 
  Elements, 
  CardNumberElement, 
  CardExpiryElement, 
  CardCvcElement, 
  useStripe, 
  useElements 
} from "@stripe/react-stripe-js";

const stripePromise = loadStripe("pk_test_51SEWfbGUJ7v3D9GXVs5sPIBEY08wZrpGmeuko8YClRovgbeQkkYJ2NpxLMtM3RyA8v7NSKM4cE8mTLthWafiYGv500lPgVJb3n");

// ... (ALL YOUR EXISTING STYLED COMPONENTS REMAIN EXACTLY THE SAME)
// Keep all your Container, Wrapper, Title, Top, etc. styled components

// Add these new styled components for separate card fields
const CardFormContainer = styled.div`
  margin: 20px 0;
  text-align: left;
`;

const CardFieldContainer = styled.div`
  margin-bottom: 15px;
`;

const CardFieldLabel = styled.label`
  display: block;
  margin-bottom: 5px;
  font-weight: 600;
  color: #333;
  font-size: 14px;
`;

const CardFieldWrapper = styled.div`
  padding: 12px;
  border: 2px solid #e1e5e9;
  border-radius: 8px;
  background: white;
  transition: border-color 0.3s;
  
  &:focus-within {
    border-color: #4CAF50;
    box-shadow: 0 0 0 2px rgba(76, 175, 80, 0.1);
  }
`;

const CardRow = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 15px;
  
  @media (max-width: 480px) {
    grid-template-columns: 1fr;
  }
`;

// Improved Stripe Payment Form Component with Separate Fields
const StripePaymentForm = ({ amount, clientSecret, onSuccess, onError }) => {
  const stripe = useStripe();
  const elements = useElements();
  const [processing, setProcessing] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    
    if (!stripe || !elements) {
      return;
    }

    setProcessing(true);
    setError('');

    try {
      const cardNumber = elements.getElement(CardNumberElement);
      const cardExpiry = elements.getElement(CardExpiryElement);
      const cardCvc = elements.getElement(CardCvcElement);

      // Create payment method with separate elements
      const { error: stripeError, paymentMethod } = await stripe.createPaymentMethod({
        type: 'card',
        card: cardNumber,
        billing_details: {
          // You can add billing details here if needed
        },
      });

      if (stripeError) {
        setError(stripeError.message);
        onError(stripeError.message);
        return;
      }

      // Confirm payment with the payment method
      const { error: confirmError, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: paymentMethod.id,
      });

      if (confirmError) {
        setError(confirmError.message);
        onError(confirmError.message);
      } else if (paymentIntent.status === 'succeeded') {
        onSuccess(paymentIntent);
      }
    } catch (err) {
      setError('Payment failed. Please try again.');
      onError(err.message);
    } finally {
      setProcessing(false);
    }
  };

  // Styling for individual card elements
  const cardElementOptions = {
    style: {
      base: {
        fontSize: '16px',
        color: '#424770',
        fontFamily: '"Helvetica Neue", Helvetica, sans-serif',
        '::placeholder': {
          color: '#aab7c4',
        },
        backgroundColor: 'transparent',
      },
      invalid: {
        color: '#9e2146',
        iconColor: '#9e2146',
      },
    },
  };

  return (
    <CardFormContainer>
      <h3 style={{ marginBottom: '15px', color: '#333' }}>Payment Details</h3>
      <p style={{ fontSize: '18px', fontWeight: '600', color: '#2c5aa0', marginBottom: '20px' }}>
        Total Amount: <span style={{ color: '#4CAF50' }}>${amount}</span>
      </p>
      
      <form onSubmit={handleSubmit}>
        {/* Card Number Field */}
        <CardFieldContainer>
          <CardFieldLabel>Card Number</CardFieldLabel>
          <CardFieldWrapper>
            <CardNumberElement
              options={{
                ...cardElementOptions,
                placeholder: '1234 1234 1234 1234',
              }}
            />
          </CardFieldWrapper>
        </CardFieldContainer>

        {/* Expiry and CVC in one row */}
        <CardRow>
          <CardFieldContainer>
            <CardFieldLabel>Expiry Date</CardFieldLabel>
            <CardFieldWrapper>
              <CardExpiryElement
                options={{
                  ...cardElementOptions,
                  placeholder: 'MM/YY',
                }}
              />
            </CardFieldWrapper>
          </CardFieldContainer>

          <CardFieldContainer>
            <CardFieldLabel>CVC</CardFieldLabel>
            <CardFieldWrapper>
              <CardCvcElement
                options={{
                  ...cardElementOptions,
                  placeholder: '123',
                }}
              />
            </CardFieldWrapper>
          </CardFieldContainer>
        </CardRow>
        
        {error && (
          <ErrorMessage style={{ textAlign: 'left' }}>
            ⚠️ {error}
          </ErrorMessage>
        )}
        
        <PayNowBtn type="submit" disabled={!stripe || processing}>
          {processing ? (
            <>
              <span style={{ marginRight: '8px' }}>⏳</span>
              Processing Payment...
            </>
          ) : (
            `Pay $${amount}`
          )}
        </PayNowBtn>
      </form>

      {/* Test Card Info - Better Styled */}
      <div style={{ 
        marginTop: '20px', 
        padding: '15px', 
        background: '#f8f9fa', 
        borderRadius: '8px', 
        fontSize: '13px',
        textAlign: 'left',
        border: '1px solid #e9ecef'
      }}>
        <h4 style={{ margin: '0 0 10px 0', color: '#495057', fontSize: '14px' }}>
          🧪 Test Card Information
        </h4>
        <div style={{ lineHeight: '1.6', color: '#6c757d' }}>
          <div><strong>Card Number:</strong> 4242 4242 4242 4242</div>
          <div><strong>Expiry Date:</strong> 12/34</div>
          <div><strong>CVC:</strong> 123</div>
          <div><strong>ZIP Code:</strong> 12345</div>
        </div>
      </div>
    </CardFormContainer>
  );
};

// Main CartPage Component (REMAINS EXACTLY THE SAME AS BEFORE)
const CartPage = () => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [isCheckingOut, setIsCheckingOut] = useState(false);
  const [showAddressForm, setShowAddressForm] = useState(false);
  const [showPayment, setShowPayment] = useState(false);
  const [paymentError, setPaymentError] = useState('');
  const [clientSecret, setClientSecret] = useState('');
  const [currentOrderId, setCurrentOrderId] = useState('');
  const navigate = useNavigate();

  const [address, setAddress] = useState({
    street: "",
    city: "",
    state: "",
    zipCode: "",
    country: ""
  });

  // Define functions at the top
  const getAuthHeader = () => {
    const token = localStorage.getItem('token');
    return token ? { Authorization: `Bearer ${token}` } : {};
  };

  const getCurrentUser = () => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const base64Payload = token.split('.')[1];
        const payload = JSON.parse(atob(base64Payload));
        return payload.userId;
      } catch (error) {
        return null;
      }
    }
    return null;
  };

  const calculateShipping = () => {
    const subtotal = getTotal();
    if (subtotal >= 50) {
      return { shipping: 0, hasFreeShipping: true };
    } else {
      return { shipping: 5.90, hasFreeShipping: false };
    }
  };

  const getTotal = () => {
    return cartItems.reduce((total, item) => total + parseFloat(item.price) * item.quantity, 0);
  };

  const getFinalTotal = () => {
    const shippingInfo = calculateShipping();
    return getTotal() + shippingInfo.shipping;
  };

  const fetchCart = useCallback(async () => {
    const userId = getCurrentUser();
    if (!userId) {
      setCartItems([]);
      return;
    }

    try {
      setLoading(true);
      const response = await axios.get(
        `${process.env.REACT_APP_API_URL}/api/cart/${userId}`,
        { headers: getAuthHeader() }
      );
      setCartItems(response.data);
    } catch (error) {
      console.error('Error fetching cart:', error);
    } finally {
      setLoading(false);
    }
  }, []);

  const updateCartItem = async (productId, quantity) => {
    const userId = getCurrentUser();
    if (!userId) return;

    try {
      await axios.put(
        `${process.env.REACT_APP_API_URL}/api/cart/${userId}`,
        { productId, quantity },
        { headers: getAuthHeader() }
      );
      await fetchCart();
    } catch (error) {
      console.error('Error updating cart:', error);
    }
  };

  const removeFromCart = async (productId) => {
    const userId = getCurrentUser();
    if (!userId) return;

    try {
      await axios.delete(
        `${process.env.REACT_APP_API_URL}/api/cart/${userId}/${productId}`,
        { headers: getAuthHeader() }
      );
      await fetchCart();
    } catch (error) {
      console.error('Error removing from cart:', error);
    }
  };

  useEffect(() => {
    fetchCart();
  }, [fetchCart]);

  const handleQuantity = (productId, quantity) => {
    if (quantity === 0) {
      removeFromCart(productId);
    } else {
      updateCartItem(productId, quantity);
    }
  };

  const handlePayment = async () => {
    setPaymentError('');
    setIsCheckingOut(true);

    try {
      const userId = getCurrentUser();
      const token = localStorage.getItem('token');
      
      const BASE_URL = process.env.REACT_APP_API_URL;
      console.log('Using base URL:', BASE_URL);

      // 1. CREATE THE ORDER FIRST
      const orderData = {
        cartItems: cartItems.map(item => ({
          productId: item.productId,
          title: item.title,
          price: item.price,
          quantity: item.quantity,
          size: item.size || 'One Size',
          color: item.color || 'Default',
          img: item.img
        })),
        address: address,
        totalAmount: getFinalTotal()
      };

      console.log('Creating order with data:', orderData);

      const orderResponse = await axios.post(
        `${BASE_URL}/api/orders`,
        orderData,
        { 
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      console.log('Order created:', orderResponse.data);
      const createdOrderId = orderResponse.data.order.id;
      setCurrentOrderId(createdOrderId);

      const amount = Math.round(getFinalTotal() * 100); // Convert to cents

      console.log('Creating payment intent for amount:', amount, 'Order ID:', createdOrderId);

      // 2. CREATE PAYMENT INTENT WITH STRIPE
      const paymentResponse = await axios.post(
        `${BASE_URL}/api/payments/create-payment-intent`,
        { 
          orderId: createdOrderId,
          amount: amount
        },
        { 
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          },
          timeout: 10000
        }
      );

      console.log('Payment intent created:', paymentResponse.data);

      if (paymentResponse.data.clientSecret) {
        setClientSecret(paymentResponse.data.clientSecret);
      } else {
        throw new Error('No client secret received from payment service');
      }

    } catch (error) {
      console.error('Payment setup error:', error);
      setPaymentError(`Payment setup failed: ${error.response?.data?.error || error.message}`);
      setIsCheckingOut(false);
    }
  };

  const handlePaymentSuccess = async (paymentIntent) => {
    try {
      const userId = getCurrentUser();
      const token = localStorage.getItem('token');
      const BASE_URL = process.env.REACT_APP_API_URL;

      console.log('Stripe payment succeeded:', paymentIntent);

      // Confirm payment with your backend
      await axios.post(
        `${BASE_URL}/api/payments/confirm-payment`,
        { paymentIntentId: paymentIntent.id },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      // Clear cart after successful payment
      try {
        await axios.delete(
          `${BASE_URL}/api/cart/${userId}/clear`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        console.log('Cart cleared successfully');
      } catch (cartError) {
        console.log('Cart clearing failed, but continuing...', cartError);
      }

      // Clear cart locally
      setCartItems([]);

      // Show success and redirect
      alert(`Payment successful! Order ID: ${currentOrderId}`);
      setShowPayment(false);
      setClientSecret('');
      navigate('/success');

    } catch (error) {
      console.error('Post-payment processing error:', error);
    }
  };

  const handlePaymentError = (errorMessage) => {
    setPaymentError(`Payment failed: ${errorMessage}`);
  };

  const handleCheckout = () => {
    if (cartItems.length === 0) {
      alert('Your cart is empty');
      return;
    }
    
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login to checkout');
      navigate('/login');
      return;
    }

    if (!showAddressForm) {
      setShowAddressForm(true);
      return;
    }

    if (!address.street || !address.city || !address.state || !address.zipCode || !address.country) {
      alert('Please fill in all address fields');
      return;
    }

    setShowPayment(true);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setAddress(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleContinueShopping = () => {
    navigate('/');
  };

  const shippingInfo = calculateShipping();

  if (loading) {
    return (
      <Container>
        <Wrapper>
          <Title>YOUR BAG</Title>
          <div style={{ textAlign: 'center', padding: '50px' }}>Loading cart...</div>
        </Wrapper>
      </Container>
    );
  }

  return (
    <Container>
      <Wrapper>
        <Title>YOUR BAG</Title>
        <Top>
          <TopButton onClick={handleContinueShopping}>CONTINUE SHOPPING</TopButton>
          <TopTexts>
            <TopText>Shopping Bag({cartItems.length})</TopText>
            <TopText>Your Wishlist (0)</TopText>
          </TopTexts>
          <TopButton 
            type="filled" 
            onClick={handleCheckout}
            disabled={cartItems.length === 0}
          >
            {isCheckingOut ? 'PROCESSING...' : 'CHECKOUT NOW'}
          </TopButton>
        </Top>
        <Bottom>
          <Info>
            {cartItems.length === 0 ? (
              <EmptyCart>
                <h3>Your cart is empty</h3>
                <p>Add some products to your cart to see them here</p>
                <TopButton onClick={handleContinueShopping} style={{ marginTop: '20px' }}>
                  START SHOPPING
                </TopButton>
              </EmptyCart>
            ) : (
              cartItems.map((item) => (
                <div key={item.id}>
                  <Product>
                    <ProductDetail>
                      <Image src={item.img || 'https://via.placeholder.com/200x200?text=No+Image'} />
                      <Details>
                        <ProductName>
                          <b>Product:</b> {item.title}
                        </ProductName>
                        <ProductId>
                          <b>ID:</b> {item.productId}
                        </ProductId>
                        {item.size && (
                          <div>
                            <b>Size:</b> {item.size}
                          </div>
                        )}
                        {item.color && (
                          <div>
                            <b>Color:</b> {item.color}
                          </div>
                        )}
                      </Details>
                    </ProductDetail>
                    <PriceDetail>
                      <ProductAmountContainer>
                        <QuantityButton onClick={() => handleQuantity(item.productId, item.quantity - 1)}>
                          -
                        </QuantityButton>
                        <ProductAmount>{item.quantity}</ProductAmount>
                        <QuantityButton onClick={() => handleQuantity(item.productId, item.quantity + 1)}>
                          +
                        </QuantityButton>
                      </ProductAmountContainer>
                      <ProductPrice>$ {(parseFloat(item.price) * item.quantity).toFixed(2)}</ProductPrice>
                    </PriceDetail>
                  </Product>
                  <Hr />
                </div>
              ))
            )}
          </Info>
          {cartItems.length > 0 && (
            <Summary>
              <SummaryTitle>ORDER SUMMARY</SummaryTitle>
              <SummaryItem>
                <SummaryItemText>Subtotal</SummaryItemText>
                <SummaryItemPrice>$ {getTotal().toFixed(2)}</SummaryItemPrice>
              </SummaryItem>
              
              <SummaryItem>
                <SummaryItemText>
                  Shipping
                  {shippingInfo.hasFreeShipping && (
                    <FreeShippingBadge>🚚 FREE</FreeShippingBadge>
                  )}
                </SummaryItemText>
                <SummaryItemPrice>
                  {shippingInfo.hasFreeShipping ? '$ 0.00' : `$ ${shippingInfo.shipping.toFixed(2)}`}
                </SummaryItemPrice>
              </SummaryItem>

              {!shippingInfo.hasFreeShipping && (
                <SummaryItem>
                  <SummaryItemText style={{ fontSize: '12px', color: '#666', fontStyle: 'italic' }}>
                    Add ${(50 - getTotal()).toFixed(2)} more for free shipping!
                  </SummaryItemText>
                  <SummaryItemPrice></SummaryItemPrice>
                </SummaryItem>
              )}

              <SummaryItem type="total">
                <SummaryItemText>Total</SummaryItemText>
                <SummaryItemPrice>$ {getFinalTotal().toFixed(2)}</SummaryItemPrice>
              </SummaryItem>

              {showAddressForm && (
                <AddressForm>
                  <h3>Shipping Address</h3>
                  <Input
                    type="text"
                    name="street"
                    placeholder="Street Address"
                    value={address.street}
                    onChange={handleInputChange}
                    required
                  />
                  <InputRow>
                    <InputGroup>
                      <Input
                        type="text"
                        name="city"
                        placeholder="City"
                        value={address.city}
                        onChange={handleInputChange}
                        required
                      />
                    </InputGroup>
                    <InputGroup>
                      <Input
                        type="text"
                        name="state"
                        placeholder="State"
                        value={address.state}
                        onChange={handleInputChange}
                        required
                      />
                    </InputGroup>
                  </InputRow>
                  <InputRow>
                    <InputGroup>
                      <Input
                        type="text"
                        name="zipCode"
                        placeholder="ZIP Code"
                        value={address.zipCode}
                        onChange={handleInputChange}
                        required
                      />
                    </InputGroup>
                    <InputGroup>
                      <Input
                        type="text"
                        name="country"
                        placeholder="Country"
                        value={address.country}
                        onChange={handleInputChange}
                        required
                      />
                    </InputGroup>
                  </InputRow>
                </AddressForm>
              )}

              <Button 
                onClick={handleCheckout}
                disabled={cartItems.length === 0 || isCheckingOut}
              >
                {isCheckingOut ? 'PROCESSING ORDER...' : 
                 showAddressForm ? 'PROCEED TO PAYMENT' : 
                 `CHECKOUT NOW ($${getFinalTotal().toFixed(2)})`}
              </Button>
            </Summary>
          )}
        </Bottom>
      </Wrapper>

      {/* Stripe Payment Modal */}
      {showPayment && (
        <PaymentModal>
          <PaymentContent>
            <h2 style={{ marginBottom: '10px', color: '#333' }}>Complete Your Purchase</h2>
            <p style={{ fontSize: '18px', fontWeight: '600', color: '#2c5aa0', marginBottom: '20px' }}>
              Total Amount: <span style={{ color: '#4CAF50' }}>${getFinalTotal().toFixed(2)}</span>
            </p>
            
            {paymentError && <ErrorMessage>{paymentError}</ErrorMessage>}
            
            {clientSecret ? (
              <Elements stripe={stripePromise} options={{ clientSecret }}>
                <StripePaymentForm 
                  amount={getFinalTotal().toFixed(2)}
                  clientSecret={clientSecret}
                  onSuccess={handlePaymentSuccess}
                  onError={handlePaymentError}
                />
              </Elements>
            ) : (
              <div>
                <PayNowBtn 
                  onClick={handlePayment} 
                  disabled={isCheckingOut}
                >
                  {isCheckingOut ? 'Setting Up Payment...' : 'Proceed to Payment'}
                </PayNowBtn>
              </div>
            )}
            
            <CancelBtn onClick={() => {
              setShowPayment(false);
              setClientSecret('');
              setPaymentError('');
            }}>
              Cancel Payment
            </CancelBtn>
          </PaymentContent>
        </PaymentModal>
      )}
    </Container>
  );
};

export default CartPage;
