import React, { useState } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  gap: 15px;
  margin-top: 20px;
`;

const QuantityContainer = styled.div`
  display: flex;
  align-items: center;
  gap: 15px;
`;

const QuantityLabel = styled.span`
  font-weight: 600;
  color: #333;
`;

const QuantityControls = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;
  border: 1px solid #ddd;
  border-radius: 5px;
  padding: 5px;
`;

const QuantityButton = styled.button`
  width: 30px;
  height: 30px;
  border: none;
  background-color: #f5f5f5;
  border-radius: 3px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  
  &:hover {
    background-color: #e0e0e0;
  }
  
  &:disabled {
    background-color: #f5f5f5;
    cursor: not-allowed;
    opacity: 0.5;
  }
`;

const Quantity = styled.span`
  width: 40px;
  text-align: center;
  font-weight: 600;
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 15px;
  
  @media (max-width: 768px) {
    flex-direction: column;
  }
`;

const PrimaryButton = styled.button`
  flex: 2;
  padding: 15px 30px;
  background-color: teal;
  color: white;
  border: none;
  border-radius: 5px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  
  &:hover {
    background-color: #018181;
  }
`;

const SecondaryButton = styled.button`
  flex: 1;
  padding: 15px 30px;
  background-color: transparent;
  color: teal;
  border: 2px solid teal;
  border-radius: 5px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  
  &:hover {
    background-color: teal;
    color: white;
  }
`;

const WishlistButton = styled.button`
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 10px 20px;
  background-color: transparent;
  color: #666;
  border: 1px solid #ddd;
  border-radius: 5px;
  cursor: pointer;
  
  &:hover {
    background-color: #f5f5f5;
    color: teal;
  }
`;

interface ProductInteractionProps {
  onAddToCart: (quantity: number) => void;
  onBuyNow: (quantity: number) => void;
  onAddToWishlist: () => void;
  maxQuantity?: number;
}

const ProductInteraction: React.FC<ProductInteractionProps> = ({
  onAddToCart,
  onBuyNow,
  onAddToWishlist,
  maxQuantity = 10
}) => {
  const [quantity, setQuantity] = useState(1);

  const handleDecrease = () => {
    if (quantity > 1) {
      setQuantity(quantity - 1);
    }
  };

  const handleIncrease = () => {
    if (quantity < maxQuantity) {
      setQuantity(quantity + 1);
    }
  };

  return (
    <Container>
      <QuantityContainer>
        <QuantityLabel>Quantity:</QuantityLabel>
        <QuantityControls>
          <QuantityButton 
            onClick={handleDecrease} 
            disabled={quantity <= 1}
          >
            -
          </QuantityButton>
          <Quantity>{quantity}</Quantity>
          <QuantityButton 
            onClick={handleIncrease} 
            disabled={quantity >= maxQuantity}
          >
            +
          </QuantityButton>
        </QuantityControls>
      </QuantityContainer>

      <ButtonGroup>
        <PrimaryButton onClick={() => onAddToCart(quantity)}>
          Add to Cart
        </PrimaryButton>
        <SecondaryButton onClick={() => onBuyNow(quantity)}>
          Buy Now
        </SecondaryButton>
      </ButtonGroup>

      <WishlistButton onClick={onAddToWishlist}>
        <i className="far fa-heart"></i>
        Add to Wishlist
      </WishlistButton>
    </Container>
  );
};

export default ProductInteraction;
