import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import styled from "styled-components";
import axios from "axios";
import Product from "./Product"; // Make sure this import is correct

const Container = styled.div`
  padding: 20px;
`;

const Title = styled.h1`
  margin: 20px;
  text-align: center;
  text-transform: capitalize;
`;

const FilterContainer = styled.div`
  display: flex;
  justify-content: space-between;
  margin: 20px;
  flex-wrap: wrap;
  gap: 15px;
`;

const Filter = styled.div`
  display: flex;
  align-items: center;
  gap: 15px;
  flex-wrap: wrap;
`;

const FilterText = styled.span`
  font-size: 18px;
  font-weight: 600;
`;

const Select = styled.select`
  padding: 8px 12px;
  border: 1px solid #ddd;
  border-radius: 4px;
  min-width: 120px;
`;

const Option = styled.option``;

const ProductContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  gap: 25px;
  padding: 20px 0;
`;

const Loading = styled.div`
  text-align: center;
  padding: 50px;
  font-size: 18px;
`;

const ProductList = () => {
  const location = useLocation();
  const category = location.pathname.split("/")[2]; // Gets 'men', 'women', 'accessories'
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    size: "",
    color: ""
  });

  // Fetch all products from your backend
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/products`);
        setProducts(response.data);
      } catch (error) {
        console.error("Error fetching products:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchProducts();
  }, []);

  // Filter products based on category
  useEffect(() => {
    if (products.length === 0) return;

    let filtered = products.filter((product) => {
      // If no category selected or 'all', show all products
      if (!category || category === "all") return true;

      // Use the existing categories array from your product data
      switch(category) {
        case "men":
          return product.categories.includes("men's clothing");
        case "women":
          return product.categories.includes("jewelery");
        case "electronics":
          return product.categories.includes("electronics");
        case "accessories":
          return product.categories.includes("electronics") || 
                 product.categories.includes("jewelery");
        default:
          return true;
      }
    });

    // Apply additional filters (size, color)
    if (filters.size) {
      filtered = filtered.filter(product => 
        product.size.includes(filters.size)
      );
    }

    if (filters.color) {
      filtered = filtered.filter(product => 
        product.color.includes(filters.color)
      );
    }

    setFilteredProducts(filtered);
  }, [products, category, filters]);

  const handleFilters = (e) => {
    const value = e.target.value;
    setFilters({
      ...filters,
      [e.target.name]: value,
    });
  };

  if (loading) {
    return (
      <Container>
        <Loading>Loading products...</Loading>
      </Container>
    );
  }

  return (
    <Container>
      <Title>{category ? `${category} Products` : "All Products"}</Title>
      
      <FilterContainer>
        <Filter>
          <FilterText>Filter Products:</FilterText>
          <Select name="size" onChange={handleFilters} value={filters.size}>
            <Option value="">All Sizes</Option>
            <Option value="S">Small (S)</Option>
            <Option value="M">Medium (M)</Option>
            <Option value="L">Large (L)</Option>
            <Option value="XL">XL</Option>
            <Option value="XXL">XXL</Option>
            <Option value="One Size">One Size</Option>
          </Select>
          
          <Select name="color" onChange={handleFilters} value={filters.color}>
            <Option value="">All Colors</Option>
            <Option value="black">Black</Option>
            <Option value="white">White</Option>
            <Option value="blue">Blue</Option>
            <Option value="red">Red</Option>
            <Option value="navy">Navy</Option>
            <Option value="gray">Gray</Option>
            <Option value="gold">Gold</Option>
            <Option value="silver">Silver</Option>
          </Select>
        </Filter>
      </FilterContainer>

      <ProductContainer>
        {filteredProducts.length > 0 ? (
          filteredProducts.map((product) => (
            <Product item={product} key={product.id} />
          ))
        ) : (
          <div style={{ textAlign: 'center', padding: '50px', color: '#666' }}>
            No products found in this category.
          </div>
        )}
      </ProductContainer>
    </Container>
  );
};

export default ProductList;
